				pv1[index].PV2[indexPV2].accommodationCode = {};
				if (pv2Seg['PV2.2']['PV2.2.1'].toString() != "") { pv1[index].PV2[indexPV2].accommodationCode.id = pv2Seg['PV2.2']['PV2.2.1']; }
