				// NTE.2, sourceOfComment
				if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { obx[index].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

				// NTE.3, comment
				obx[index].NTE[indexNTE].comment = [];
				for each (field in nteSeg['NTE.3']) {
					var block = {};
