		if (seg['DG1.19']['DG1.19.1'].toString() != "") { dg1[index].attestationDateTime = seg['DG1.19']['DG1.19.1']; }

		// clean up DG1 and increment index
		if (Object.keys(dg1[index]).length == 0) {
			delete dg1[index];
		} else {
			index++;
		}

	}
}

