						drg[index].PR1[indexPR1].ROL[indexROL].rolePerson = {};
						if (rolSeg['ROL.4']['ROL.4.1'].toString() != "") { drg[index].PR1[indexPR1].ROL[indexROL].rolePerson.id = rolSeg['ROL.4']['ROL.4.1']; }
						if (rolSeg['ROL.4']['ROL.4.2'].toString() != "") { drg[index].PR1[indexPR1].ROL[indexROL].rolePerson.lastName = rolSeg['ROL.4']['ROL.4.2']; }
						if (rolSeg['ROL.4']['ROL.4.3'].toString() != "") { drg[index].PR1[indexPR1].ROL[indexROL].rolePerson.firstName = rolSeg['ROL.4']['ROL.4.3']; }
