						if (rolSeg['ROL.8']['ROL.8.4'].toString() != "") { drg[index].PR1[indexPR1].ROL[indexROL].roleActionReason.altId = rolSeg['ROL.8']['ROL.8.4']; }
						if (rolSeg['ROL.8']['ROL.8.5'].toString() != "") { drg[index].PR1[indexPR1].ROL[indexROL].roleActionReason.altText = rolSeg['ROL.8']['ROL.8.5']; }
