		if (seg['IN1.40']['IN1.40.6']['IN1.40.6.3'].toString() != "") { in1[index].roomRateSemiPrivate.rangeType.codingSystem = seg['IN1.40']['IN1.40.6']['IN1.40.6.3']; }
