		if (seg['IN1.41']['IN1.41.3'].toString() != "") { in1[index].roomRatePrivate.fromValue = seg['IN1.41']['IN1.41.3']; }
		if (seg['IN1.41']['IN1.41.4'].toString() != "") { in1[index].roomRatePrivate.toValue = seg['IN1.41']['IN1.41.4']; }
