		if (seg['IN1.42']['IN1.42.5'].toString() != "") { in1[index].insuredEmploymentStatus.altText = seg['IN1.42']['IN1.42.5']; }
		if (seg['IN1.42']['IN1.42.6'].toString() != "") { in1[index].insuredEmploymentStatus.altCodingSystem = seg['IN1.42']['IN1.42.6']; }
