		// IN1.46, priorInsurancePlanId
		if (seg['IN1.46']['IN1.46.1'].toString() != "") { in1[index].priorInsurancePlanId = seg['IN1.46']['IN1.46.1']; }

		// IN1.47, coverageType
		if (seg['IN1.47']['IN1.47.1'].toString() != "") { in1[index].coverageType = seg['IN1.47']['IN1.47.1']; }

		// IN1.48, handicap
