				} else {
					indexIN2++;
				}
			}

			// Clean up the IN2 object if it's empty
			if (in1[index].IN2[0] == null){
				delete in1[index].IN2;
			}
		}

		// Increment the IN1 index
		index++;
	}
}

// Optional: save a copy of the completed IN1 segment for reference later
//channelMap.put("in1", in1); // uncomment to save

// Re-add the IN1 segment to the hl7JSON object
if (in1 && Object.keys(in1).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.IN1 = in1;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

entry.string:
// Pull out the PV1 JSON to embed the IN1 segments in
var in1 = JSON.parse($('hl7JSON')).content.IN1;

// IN1
if (msg['IN1'].length() > 0) {

	// IN1 is a required to have a IN2, but if the IN1 was blank and was removed from the JSON, just create a new one to add the IN2 into
	if (!in1) {
		in1 = [{}];
	}

	var index = 0;
	for each (seg in msg.IN1) {

		// IN2 (IN1)
		var in2Group = getSegmentsAfter(msg,seg,'IN2',true);
		if (in2Group.length > 0 && in1) {
			in1[index].IN2 = [];
			var indexIN2 = 0;
			for each (in2Seg in in2Group) {
				in1[index].IN2[indexIN2] = {};

				// IN2.1, insuredEmployeeId
				in1[index].IN2[indexIN2].insuredEmployeeId = [];
				for each (field in in2Seg['IN2.1']) {
					var block = {};
					if (field['IN2.1.1'].toString() != "") { block.id = field['IN2.1.1']; }
					if (field['IN2.1.2'].toString() != "") { block.identifierCheckDigit = field['IN2.1.2']; }
					if (field['IN2.1.3'].toString() != "") { block.codeIdentifyingCheckDigitScheme = field['IN2.1.3']; }
