					if (field['IN3.3.9']['IN3.3.9.2'].toString() != "") { block.assigningAuthority.universalId = field['IN3.3.9']['IN3.3.9.2']; }
					if (field['IN3.3.9']['IN3.3.9.3'].toString() != "") { block.assigningAuthority.universalIdType = field['IN3.3.9']['IN3.3.9.3']; }
