				if (in3Seg['IN3.15']['IN3.15.1'].toString() != "") { in1[index].IN3[indexIN3].certificationContact = in3Seg['IN3.15']['IN3.15.1']; }

				// IN3.16, certificationContactPhoneNumber
				in1[index].IN3[indexIN3].certificationContactPhoneNumber = [];
