					if (field['IN3.3.6'].toString() != "") { block.prefix = field['IN3.3.6']; }
					if (field['IN3.3.7'].toString() != "") { block.degree = field['IN3.3.7']; }
					if (field['IN3.3.8'].toString() != "") { block.sourceTable = field['IN3.3.8']; }
