	    if (seg['UB2.1']['UB2.1.1'].toString() != "") { ub2[hl7JSON].setId = seg['UB2.1']['UB2.1.1']; }

	    // UB2.2, coinsuranceDays
	    if (seg['UB2.2']['UB2.2.1'].toString() != "") { ub2[hl7JSON].coinsuranceDays = seg['UB2.2']['UB2.2.1']; }

	    // UB2.3, conditionCode
	    ub2[hl7JSON].conditionCode = [];
	    for each (field in seg['UB2.3']) {
	    	if (field['UB2.3.1'].toString() != "") {
	    		ub2[hl7JSON].conditionCode.push(field['UB2.3.1']);
	    	}
	    }
	    if (ub2[hl7JSON].conditionCode.length == 0) {
	    	delete ub2[hl7JSON].conditionCode;
	    }

	    // UB2.4, coveredDays
	    if (seg['UB2.4']['UB2.4.1'].toString() != "") { ub2[hl7JSON].coveredDays = seg['UB2.4']['UB2.4.1']; }

	    // UB2.5, nonCoveredDays
	    if (seg['UB2.5']['UB2.5.1'].toString() != "") { ub2[hl7JSON].nonCoveredDays = seg['UB2.5']['UB2.5.1']; }

	    // UB2.6, valueAmountAndCode
	    ub2[hl7JSON].valueAmountAndCode = [];
	    for each (field in seg['UB2.6']) {
	    	var block = {};
	    	if (field['UB2.6.1'].toString() != "") { block.valueCode = field['UB2.6.1']; }
	    	if (field['UB2.6.2'].toString() != "") { block.valueAmount = field['UB2.6.2']; }
	    	if (Object.keys(block).length > 0) {
	    		ub2[hl7JSON].valueAmountAndCode.push(block);
	    	}
	    }
	    if (ub2[hl7JSON].valueAmountAndCode.length == 0) {
	    	delete ub2[hl7JSON].valueAmountAndCode;
	    }

	    // UB2.7, occurrence
	    ub2[hl7JSON].occurrence = [];
	    for each (field in seg['UB2.7']) {
	    	var block = {};
	    	block.occurrenceCode = {};
	    	if (field['UB2.7.1']['UB2.7.1.1'].toString() != "") { block.occurrenceCode.id = field['UB2.7.1']['UB2.7.1.1']; }
	    	if (field['UB2.7.1']['UB2.7.1.2'].toString() != "") { block.occurrenceCode.text = field['UB2.7.1']['UB2.7.1.2']; }
	    	if (field['UB2.7.1']['UB2.7.1.3'].toString() != "") { block.occurrenceCode.codingSystem = field['UB2.7.1']['UB2.7.1.3']; }
	    	if (field['UB2.7.1']['UB2.7.1.4'].toString() != "") { block.occurrenceCode.altId = field['UB2.7.1']['UB2.7.1.4']; }
	    	if (field['UB2.7.1']['UB2.7.1.5'].toString() != "") { block.occurrenceCode.altText = field['UB2.7.1']['UB2.7.1.5']; }
	    	if (field['UB2.7.1']['UB2.7.1.6'].toString() != "") { block.occurrenceCode.altCodingSystem = field['UB2.7.1']['UB2.7.1.6']; }
	    	if (Object.keys(block.occurrenceCode).length == 0) {
	    		if (field['UB2.7.1'].toString() != "") { block.occurrenceCode.id = field['UB2.7.1']; }
			else delete block.occurrenceSpanCode;
	    	}
	    	if (field['UB2.7.2'].toString() != "") { block.occurrenceDate = field['UB2.7.2']; }
	    	if (Object.keys(block).length > 0) {
	    		ub2[hl7JSON].occurrence.push(block);
	    	}
	    }
	    if (ub2[hl7JSON].occurrence.length == 0) {
	    	delete ub2[hl7JSON].occurrence;
	    }

	    // UB2.8, occurrenceSpanCodeAndDate
	    ub2[hl7JSON].occurrenceSpanCodeAndDate = [];
	    for each (field in seg['UB2.8']) {
	    	var block = {};
	    	block.occurrenceSpanCode = {};
	    	if (field['UB2.8.1']['UB2.8.1.1'].toString() != "") { block.occurrenceSpanCode.id = field['UB2.8.1']['UB2.8.1.1']; }
	    	if (field['UB2.8.1']['UB2.8.1.2'].toString() != "") { block.occurrenceSpanCode.text = field['UB2.8.1']['UB2.8.1.2']; }
	    	if (field['UB2.8.1']['UB2.8.1.3'].toString() != "") { block.occurrenceSpanCode.codingSystem = field['UB2.8.1']['UB2.8.1.3']; }
	    	if (field['UB2.8.1']['UB2.8.1.4'].toString() != "") { block.occurrenceSpanCode.altId = field['UB2.8.1']['UB2.8.1.4']; }
	    	if (field['UB2.8.1']['UB2.8.1.5'].toString() != "") { block.occurrenceSpanCode.altText = field['UB2.8.1']['UB2.8.1.5']; }
	    	if (field['UB2.8.1']['UB2.8.1.6'].toString() != "") { block.occurrenceSpanCode.altCodingSystem = field['UB2.8.1']['UB2.8.1.6']; }
	    	if (Object.keys(block.occurrenceSpanCode).length == 0) {
	    		if (field['UB2.8.1'].toString() != "") { block.occurrenceSpanCode.id = field['UB2.8.1']; }
			else delete block.occurrenceSpanCode;
	    	}
	    	if (field['UB2.8.2'].toString() != "") { block.occurrenceSpanStartDate = field['UB2.8.2']; }
	    	if (field['UB2.8.3'].toString() != "") { block.occurrenceSpanEndDate = field['UB2.8.3']; }
	    	if (Object.keys(block).length > 0) {
	    		ub2[hl7JSON].occurrenceSpanCodeAndDate.push(block);
	    	}
	    }
	    if (ub2[hl7JSON].occurrenceSpanCodeAndDate.length == 0) {
	    	delete ub2[hl7JSON].occurrenceSpanCodeAndDate;
	    }

	    // UB2.9, UB92Locator2
	    ub2[hl7JSON].UB92Locator2 = [];
	    for each (field in seg['UB2.9']) {
	    	if (field['UB2.9.1'].toString() != "") {
	    		ub2[hl7JSON].UB92Locator2.push(field['UB2.9.1']);
	    	}
	    }
	    if (ub2[hl7JSON].UB92Locator2.length == 0) {
	    	delete ub2[hl7JSON].UB92Locator2;
	    }


	    // UB2.10, UB92Locator11
	    ub2[hl7JSON].UB92Locator11 = [];
	    for each (field in seg['UB2.10']) {
	    	if (field['UB2.10.1'].toString() != "") {
	    		ub2[hl7JSON].UB92Locator11.push(field['UB2.10.1']);
	    	}
	    }
	    if (ub2[hl7JSON].UB92Locator11.length == 0) {
	    	delete ub2[hl7JSON].UB92Locator11;
	    }

	    // UB2.11, UB92Locator31
	    if (seg['UB2.11']['UB2.11.1'].toString() != "") { ub2[hl7JSON].UB92Locator31 = seg['UB2.11']['UB2.11.1']; }

