if (msg['PV1'].length() > 0) {
	pv1 = [];
	var index = 0;
	for each (seg in msg.PV1) {
		pv1[index] = {};

		// PV1.1, setId
		if (seg['PV1.1']['PV1.1.1'].toString() != "") { pv1[index].setId = seg['PV1.1']['PV1.1.1']; }

