					delete txa[index].OBX[indexOBX].natureOfAbnormal;
				}

				// OBX.11, observeResultStatus
				if (obxSeg['OBX.11']['OBX.11.1'].toString() != "") { txa[index].OBX[indexOBX].observeResultStatus = obxSeg['OBX.11']['OBX.11.1']; }

				// OBX.12, effectiveDateLastNormalValue
				if (obxSeg['OBX.12']['OBX.12.1'].toString() != "") { txa[index].OBX[indexOBX].effectiveDateLastNormalValue = obxSeg['OBX.12']['OBX.12.1']; }

				// OBX.13, definedAccessChecks
				if (obxSeg['OBX.13']['OBX.13.1'].toString() != "") { txa[index].OBX[indexOBX].definedAccessChecks = obxSeg['OBX.13']['OBX.13.1']; }

				// OBX.14, observationDateTime
				if (obxSeg['OBX.14']['OBX.14.1'].toString() != "") { txa[index].OBX[indexOBX].observationDateTime = obxSeg['OBX.14']['OBX.14.1']; }

				// OBX.15, producersId
				txa[index].OBX[indexOBX].producersId = {};
				if (obxSeg['OBX.15']['OBX.15.1'].toString() != "") { txa[index].OBX[indexOBX].producersId.id = obxSeg['OBX.15']['OBX.15.1']; }
				if (obxSeg['OBX.15']['OBX.15.2'].toString() != "") { txa[index].OBX[indexOBX].producersId.text = obxSeg['OBX.15']['OBX.15.2']; }
				if (obxSeg['OBX.15']['OBX.15.3'].toString() != "") { txa[index].OBX[indexOBX].producersId.codingSystem = obxSeg['OBX.15']['OBX.15.3']; }
				if (obxSeg['OBX.15']['OBX.15.4'].toString() != "") { txa[index].OBX[indexOBX].producersId.altId = obxSeg['OBX.15']['OBX.15.4']; }
				if (obxSeg['OBX.15']['OBX.15.5'].toString() != "") { txa[index].OBX[indexOBX].producersId.altText = obxSeg['OBX.15']['OBX.15.5']; }
				if (obxSeg['OBX.15']['OBX.15.6'].toString() != "") { txa[index].OBX[indexOBX].producersId.altCodingSystem = obxSeg['OBX.15']['OBX.15.6']; }
				if (Object.keys(txa[index].OBX[indexOBX].producersId).length == 0) {
					delete txa[index].OBX[indexOBX].producersId;
				}

				// OBX.16, responsibleObserver
				txa[index].OBX[indexOBX].responsibleObserver = {};
				if (obxSeg['OBX.16']['OBX.16.1'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.id = obxSeg['OBX.16']['OBX.16.1']; }
				if (obxSeg['OBX.16']['OBX.16.2'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.lastName = obxSeg['OBX.16']['OBX.16.2']; }
				if (obxSeg['OBX.16']['OBX.16.3'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.firstName = obxSeg['OBX.16']['OBX.16.3']; }
				if (obxSeg['OBX.16']['OBX.16.4'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.middleInitOrName = obxSeg['OBX.16']['OBX.16.4']; }
				if (obxSeg['OBX.16']['OBX.16.5'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.suffix = obxSeg['OBX.16']['OBX.16.5']; }
				if (obxSeg['OBX.16']['OBX.16.6'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.prefix = obxSeg['OBX.16']['OBX.16.6']; }
