				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalIdType = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3']; }
				if (Object.keys(txa[index].OBX[indexOBX].responsibleObserver.assigningFacility).length == 0) {
