				txa[index].OBX[indexOBX].responsibleObserver = {};
				if (obxSeg['OBX.16']['OBX.16.1'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.id = obxSeg['OBX.16']['OBX.16.1']; }
				if (obxSeg['OBX.16']['OBX.16.2'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.lastName = obxSeg['OBX.16']['OBX.16.2']; }
				if (obxSeg['OBX.16']['OBX.16.3'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.firstName = obxSeg['OBX.16']['OBX.16.3']; }
				if (obxSeg['OBX.16']['OBX.16.4'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.middleInitOrName = obxSeg['OBX.16']['OBX.16.4']; }
				if (obxSeg['OBX.16']['OBX.16.5'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.suffix = obxSeg['OBX.16']['OBX.16.5']; }
				if (obxSeg['OBX.16']['OBX.16.6'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.prefix = obxSeg['OBX.16']['OBX.16.6']; }
				if (obxSeg['OBX.16']['OBX.16.7'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.degree = obxSeg['OBX.16']['OBX.16.7']; }
				if (obxSeg['OBX.16']['OBX.16.8'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.sourceTable = obxSeg['OBX.16']['OBX.16.8']; }
				txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority = {};
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1']; }
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority.universalId = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2']; }
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority.universalIdType = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3']; }
				if (Object.keys(txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority).length == 0) {
					if (obxSeg['OBX.16']['OBX.16.9'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']; }
					else delete txa[index].OBX[indexOBX].responsibleObserver.assigningAuthority;
				}
				if (obxSeg['OBX.16']['OBX.16.10'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.nameTypeCode = obxSeg['OBX.16']['OBX.16.10']; }
				if (obxSeg['OBX.16']['OBX.16.11'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.identifierCheckDigit = obxSeg['OBX.16']['OBX.16.11']; }
				if (obxSeg['OBX.16']['OBX.16.12'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.codeIdentifyingCheckDigitScheme = obxSeg['OBX.16']['OBX.16.12']; }
				if (obxSeg['OBX.16']['OBX.16.13'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.identifierTypeCode = obxSeg['OBX.16']['OBX.16.13']; }
				txa[index].OBX[indexOBX].responsibleObserver.assigningFacility = {};
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1']; }
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalId = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2']; }
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalIdType = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3']; }
				if (Object.keys(txa[index].OBX[indexOBX].responsibleObserver.assigningFacility).length == 0) {
					if (obxSeg['OBX.16']['OBX.16.14'].toString() != "") { txa[index].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']; }
					else delete txa[index].OBX[indexOBX].responsibleObserver.assigningFacility;
				}
				if (Object.keys(txa[index].OBX[indexOBX].responsibleObserver).length == 0) {
					delete txa[index].OBX[indexOBX].responsibleObserver;
				}

				// OBX.17, observationMethod
				txa[index].OBX[indexOBX].observationMethod = [];
				for each (field in obxSeg['OBX.17']) {
					var block = {};
					if (field['OBX.17.1'].toString() != "") { block.id = field['OBX.17.1']; }
					if (field['OBX.17.2'].toString() != "") { block.text = field['OBX.17.2']; }
					if (field['OBX.17.3'].toString() != "") { block.codingSystem = field['OBX.17.3']; }
					if (field['OBX.17.4'].toString() != "") { block.altId = field['OBX.17.4']; }
					if (field['OBX.17.5'].toString() != "") { block.altText = field['OBX.17.5']; }
					if (field['OBX.17.6'].toString() != "") { block.altCodingSystem = field['OBX.17.6']; }
					if (Object.keys(block).length > 0) {
						txa[index].OBX[indexOBX].observationMethod.push(block);
					}
				}
				if (txa[index].OBX[indexOBX].observationMethod.length == 0) {
					delete txa[index].OBX[indexOBX].observationMethod;
				}

				// NTE (OBX)
				var nteGroup = getSegmentsAfter(msg,obxSeg,'NTE',true);
				if (nteGroup.length > 0) {
					txa[index].OBX[indexOBX].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						txa[index].OBX[indexOBX].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { txa[index].OBX[indexOBX].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { txa[index].OBX[indexOBX].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						txa[index].OBX[indexOBX].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								txa[index].OBX[indexOBX].NTE[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (txa[index].OBX[indexOBX].NTE[indexNTE].comment.length == 0) {
							delete txa[index].OBX[indexOBX].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (txa[index].OBX[indexOBX].NTE.length == 0) {
							delete txa[index].OBX[indexOBX].NTE;
						} else {
							indexNTE++;
						}
					}
				}

				// clean up OBX and increment index
				if (Object.keys(txa[index].OBX[indexOBX]).length == 0) {
					delete txa[index].OBX[indexOBX];
				} else {
					indexOBX++;
				}
	    	}
	    }

	    // clean up TXA and increment index
	    if (Object.keys(txa[index]).length == 0) {
			delete txa[index];
		} else {
			index++;
		}
	}
}

// Optional: save a copy of the completed TXA segment for reference later
//channelMap.put("txa", txa); // uncomment to save

// If it has content, add the TXA segment to the hl7JSON object
if (txa && Object.keys(txa).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.TXA = txa;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

channel.undeployScript:
