step.script:
// NTE
var nte;
var nteGroup = getSegmentsAfter(msg, msg['MSH'], 'NTE', true);
if (msg['NTE'].length() > 0) {
	nte = [];
	var index = 0;

	for each (seg in nteGroup) {
		nte[index] = {};

		// NTE.1, setId
		if (seg['NTE.1']['NTE.1.1'].toString() != "") { nte[index].setId = seg['NTE.1']['NTE.1.1']; }

		// NTE.2, sourceOfComment
		if (seg['NTE.2']['NTE.2.1'].toString() != "") { nte[index].sourceOfComment = seg['NTE.2']['NTE.2.1']; }

		// NTE.3, comment
		nte[index].comment = [];
		for each (field in seg['NTE.3']) {
			var block = {};
			if (field['NTE.3.1'].toString() != "") { 
				nte[index].comment.push(field['NTE.3.1']);
			}
		}
		if (nte[index].comment.length == 0) {
			delete nte[index].comment;
		}

		// clean up NTE and increment index
		if (Object.keys(nte[index]).length == 0) {
			delete nte[index];
		} else {
			index++;
		}
	}
}

// Optional: save a copy of the completed NTE segment for reference later
//channelMap.put("nte", nte); // uncomment to save

// If it has content, add the MSH segment to the hl7JSON object
if (msh && Object.keys(msh).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.NTE = nte;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

