// If it has content, add the PID segment to the hl7JSON object
if (pid && Object.keys(pid).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.PID = pid;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

