					if (pv1Seg['PV1.50']['PV1.50.4'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority.namespaceId = pv1Seg['PV1.50']['PV1.50.4']; }
					else delete pid[index].PV1[indexPV1].alternateVisitID.assigningAuthority;
