						// PV2.17, purgeStatusDate
						if (pv2Seg['PV2.17']['PV2.17.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].purgeStatusDate = pv2Seg['PV2.17']['PV2.17.1']; }

