						// PV2.22, visitProtectionIndicator
						if (pv2Seg['PV2.22']['PV2.22.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].visitProtectionIndicator = pv2Seg['PV2.22']['PV2.22.1']; }

