var in1 = JSON.parse($('hl7JSON')).content.IN1;

// IN1
if (msg['IN1'].length() > 0) {

	// IN1 is a required to have a IN3, but if the IN1 was blank and was removed from the JSON, just create a new one to add the IN3 into
	if (!in1) {
		in1 = [{}];
	}

	var index = 0;
	for each (seg in msg.IN1) {

		// IN3 (IN1)
		var in3Group = getSegmentsAfter(msg,seg,'IN3',true);
		if (in3Group.length > 0 && in1) {
			in1[index].IN3 = [];
			var indexIN3 = 0;
			for each (in3Seg in in3Group) {
