		if (seg['GT1.55']['GT1.55.1'].toString() != "") { gt1[index].guarantorRace = seg['GT1.55']['GT1.55.1']; }

		// clean up GT1 and increment index
		if (Object.keys(gt1[index]).length == 0) {
			delete gt1[index];
		} else {
			index++;
		}
	}
}

