				if (blgSeg['BLG.3']['BLG.3.2'].toString() != "") { orc[index].BLG[indexBLG].accountId.identifierCheckDigit = blgSeg['BLG.3']['BLG.3.2']; }
				if (blgSeg['BLG.3']['BLG.3.3'].toString() != "") { orc[index].BLG[indexBLG].accountId.codeIdentifyingCheckDigitScheme = blgSeg['BLG.3']['BLG.3.3']; }
				orc[index].BLG[indexBLG].accountId.assigningAuthority = {};
				if (blgSeg['BLG.3']['BLG.3.4']['BLG.3.4.1'].toString() != "") { orc[index].BLG[indexBLG].accountId.assigningAuthority.namespaceId = blgSeg['BLG.3']['BLG.3.4']['BLG.3.4.1']; }
				if (blgSeg['BLG.3']['BLG.3.4']['BLG.3.4.2'].toString() != "") { orc[index].BLG[indexBLG].accountId.assigningAuthority.universalId = blgSeg['BLG.3']['BLG.3.4']['BLG.3.4.2']; }
				if (blgSeg['BLG.3']['BLG.3.4']['BLG.3.4.3'].toString() != "") { orc[index].BLG[indexBLG].accountId.assigningAuthority.universalIdType = blgSeg['BLG.3']['BLG.3.4']['BLG.3.4.3']; }
				if (Object.keys(orc[index].BLG[indexBLG].accountId.assigningAuthority).length == 0) {
					if (blgSeg['BLG.3']['BLG.3.4'].toString() != "") { orc[index].BLG[indexBLG].accountId.assigningAuthority.namespaceId = blgSeg['BLG.3']['BLG.3.4']; }
					else delete orc[index].BLG[indexBLG].accountId.assigningAuthority;
				}
				if (Object.keys(orc[index].BLG[indexBLG].accountId).length == 0) {
					delete orc[index].BLG[indexBLG].accountId;
				}

				// clean up BLG and increment index
				if (Object.keys(orc[index].BLG[indexBLG]).length == 0) {
					delete orc[index].BLG[indexBLG];
				} else {
					indexBLG++;
				}
			}
		}

		// clean up ORC and increment index
		if (Object.keys(orc[index]).length == 0) {
			delete orc[index];
		} else {
			index++;
		}
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// If it has content, add the AL1 segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the OBR segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC is a required to have a OBR, but if the ORC was blank and was removed from the JSON, just create a new one to add the OBR into
if (!orc) {
	orc = [{}];
}

// ORC
if (msg['ORC'].length() > 0) {
	var index = 0;
	for each (seg in msg.ORC) {

		// OBR (ORC for ORM)
		var obrGroup = getSegmentsAfter(msg,seg,'OBR',false);
		if (obrGroup.length > 0) {
			orc[index].OBR = [];
			var indexOBR = 0;
			for each (obrSeg in obrGroup) {
				orc[index].OBR[indexOBR] = {};

				// OBR.1, setId
				if (obrSeg['OBR.1']['OBR.1.1'].toString() != "") { orc[index].OBR[indexOBR].setId = obrSeg['OBR.1']['OBR.1.1']; }

				// OBR.2, placerOrderNumber
				orc[index].OBR[indexOBR].placerOrderNumber = {};
