var pid;
if (msg['PID'].length() > 0) {
	pid = [];
	var index = 0;
	for each (seg in msg.PID) {
		pid[index] = {};

		// PID.1, setId
		if (seg['PID.1']['PID.1.1'].toString() != "") { pid[index].setId = seg['PID.1']['PID.1.1']; }

