				pid[index].PV1[indexPV1].alternateVisitID = {};
				if (pv1Seg['PV1.50']['PV1.50.1'].toString() != "") { pid[index].PV1[indexPV1].alternateVisitID.id = pv1Seg['PV1.50']['PV1.50.1']; }
