						// PV2.37, babyDetainedIndicator
						if (pv2Seg['PV2.37']['PV2.37.1'].toString() != "") { pid[index].PV1[indexPV1].PV2[indexPV2].babyDetainedIndicator = pv2Seg['PV2.37']['PV2.37.1']; }

						// clean up PV2 and increment index
						if (Object.keys(pid[index].PV1[indexPV1].PV2[indexPV2]).length == 0) {
							delete pid[index].PV1[indexPV1].PV2[indexPV2];
						} else {
							indexPV2++;
						}
					}

					// Clean up the PV2 object if it's empty
					if (pid[index].PV1[indexPV1].PV2[0] == null){
						delete pid[index].PV1[indexPV1].PV2;
					}
				}
			}

			// Increment the PV1 index
			indexPV1++;
		}

		// Increment the PID index
		index++;
	}
}

// Optional: save a copy of the completed PID segment for reference later
//channelMap.put("pid", pid); // uncomment to save

// Re-add the PID segment to the hl7JSON object
if (pid && Object.keys(pid).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.PID = pid;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

entry.string:
// Pull out the PID JSON to embed the PV1 segments in
var pid = JSON.parse($('hl7JSON')).content.PID;

// PID iteration, in order to grab related PV1's
if (msg['PID'].length() > 0) {

	// PID is required to have a PV1, but if the PID was blank and was removed from the JSON, just create a new one to add the PV1 into
	if (!pid) {
		pid = [{}];
	}


	var index = 0;
	for each (seg in msg.PID) {

		// PV1 (PID)
		var pv1Group = getSegmentsAfter(msg,seg,'PV1',false);
		if (pv1Group.length > 0 && pid) {
			var indexPV1 = 0;

			// PV1 is required for PV2, so create it if it's not already present in the PID object
			if (!pid[index].PV1) {
				pid[index].PV1 = [{}];
			}

			for each (pv1Seg in pv1Group) {

				// PV2 (PV1) [PID]
				var pv2Group = getSegmentsAfter(msg,pv1Seg,'PV2',true);
				if (pv2Group.length > 0 && pid[index].PV1) {
					pid[index].PV1[indexPV1].PV2 = [];
					var indexPV2 = 0;
					for each (pv2Seg in pv2Group) {
						pid[index].PV1[indexPV1].PV2[indexPV2] = {};

						// PV2.1, priorPendingLocation
						pid[index].PV1[indexPV1].PV2[indexPV2].priorPendingLocation = {};
