		if (seg['ORC.19']['ORC.19.9']['ORC.19.9.1'].toString() != "") { orc[index].actionBy.assigningAuthority.id = seg['ORC.19']['ORC.19.9']['ORC.19.9.1']; }
		if (seg['ORC.19']['ORC.19.9']['ORC.19.9.2'].toString() != "") { orc[index].actionBy.assigningAuthority.universalId = seg['ORC.19']['ORC.19.9']['ORC.19.9.2']; }
		if (seg['ORC.19']['ORC.19.9']['ORC.19.9.3'].toString() != "") { orc[index].actionBy.assigningAuthority.universalIdType = seg['ORC.19']['ORC.19.9']['ORC.19.9.3']; }
