		if (seg['ORC.19']['ORC.19.12'].toString() != "") { orc[index].actionBy.codeIdentifyingCheckDigitScheme = seg['ORC.19']['ORC.19.12']; }
		if (seg['ORC.19']['ORC.19.13'].toString() != "") { orc[index].actionBy.identifierTypeCode = seg['ORC.19']['ORC.19.13']; }
		orc[index].actionBy.assigningFacility = {};
		if (seg['ORC.19']['ORC.19.14']['ORC.19.14.1'].toString() != "") { orc[index].actionBy.assigningFacility.id = seg['ORC.19']['ORC.19.14']['ORC.19.14.1']; }
		if (seg['ORC.19']['ORC.19.14']['ORC.19.14.2'].toString() != "") { orc[index].actionBy.assigningFacility.universalId = seg['ORC.19']['ORC.19.14']['ORC.19.14.2']; }
