		// ORC.13, enterersLocation
		orc[index].enterersLocation = {};
		if (seg['ORC.13']['ORC.13.1'].toString() != "") { orc[index].enterersLocation.pointOfCare = seg['ORC.13']['ORC.13.1']; }
