		if (seg['ORC.17']['ORC.17.1'].toString() != "") { orc[index].enteringOrganization.id = seg['ORC.17']['ORC.17.1']; }
		if (seg['ORC.17']['ORC.17.2'].toString() != "") { orc[index].enteringOrganization.text = seg['ORC.17']['ORC.17.2']; }
		if (seg['ORC.17']['ORC.17.3'].toString() != "") { orc[index].enteringOrganization.codingSystem = seg['ORC.17']['ORC.17.3']; }
		if (seg['ORC.17']['ORC.17.4'].toString() != "") { orc[index].enteringOrganization.altId = seg['ORC.17']['ORC.17.4']; }
		if (seg['ORC.17']['ORC.17.5'].toString() != "") { orc[index].enteringOrganization.altText = seg['ORC.17']['ORC.17.5']; }
		if (seg['ORC.17']['ORC.17.6'].toString() != "") { orc[index].enteringOrganization.altCodingSystem = seg['ORC.17']['ORC.17.6']; }
		if (Object.keys(orc[index].enteringOrganization).length == 0) {
			delete orc[index].enteringOrganization;
		}

		// ORC.18, enteringDevice
		orc[index].enteringDevice = {};
		if (seg['ORC.18']['ORC.18.1'].toString() != "") { orc[index].enteringDevice.id = seg['ORC.18']['ORC.18.1']; }
		if (seg['ORC.18']['ORC.18.2'].toString() != "") { orc[index].enteringDevice.text = seg['ORC.18']['ORC.18.2']; }
		if (seg['ORC.18']['ORC.18.3'].toString() != "") { orc[index].enteringDevice.codingSystem = seg['ORC.18']['ORC.18.3']; }
		if (seg['ORC.18']['ORC.18.4'].toString() != "") { orc[index].enteringDevice.altId = seg['ORC.18']['ORC.18.4']; }
		if (seg['ORC.18']['ORC.18.5'].toString() != "") { orc[index].enteringDevice.altText = seg['ORC.18']['ORC.18.5']; }
		if (seg['ORC.18']['ORC.18.6'].toString() != "") { orc[index].enteringDevice.altCodingSystem = seg['ORC.18']['ORC.18.6']; }
