			delete orc[index].enteringDevice;
		}

		// ORC.19, actionBy
		orc[index].actionBy = {};
		if (seg['ORC.19']['ORC.19.1'].toString() != "") { orc[index].actionBy.id = seg['ORC.19']['ORC.19.1']; }
		if (seg['ORC.19']['ORC.19.2'].toString() != "") { orc[index].actionBy.lastName = seg['ORC.19']['ORC.19.2']; }
		if (seg['ORC.19']['ORC.19.3'].toString() != "") { orc[index].actionBy.firstName = seg['ORC.19']['ORC.19.3']; }
		if (seg['ORC.19']['ORC.19.4'].toString() != "") { orc[index].actionBy.middleInitOrName = seg['ORC.19']['ORC.19.4']; }
		if (seg['ORC.19']['ORC.19.5'].toString() != "") { orc[index].actionBy.suffix = seg['ORC.19']['ORC.19.5']; }
		if (seg['ORC.19']['ORC.19.6'].toString() != "") { orc[index].actionBy.prefix = seg['ORC.19']['ORC.19.6']; }
		if (seg['ORC.19']['ORC.19.7'].toString() != "") { orc[index].actionBy.degree = seg['ORC.19']['ORC.19.7']; }
		if (seg['ORC.19']['ORC.19.8'].toString() != "") { orc[index].actionBy.sourceTable = seg['ORC.19']['ORC.19.8']; }
		orc[index].actionBy.assigningAuthority = {};
		if (seg['ORC.19']['ORC.19.9']['ORC.19.9.1'].toString() != "") { orc[index].actionBy.assigningAuthority.id = seg['ORC.19']['ORC.19.9']['ORC.19.9.1']; }
		if (seg['ORC.19']['ORC.19.9']['ORC.19.9.2'].toString() != "") { orc[index].actionBy.assigningAuthority.universalId = seg['ORC.19']['ORC.19.9']['ORC.19.9.2']; }
		if (seg['ORC.19']['ORC.19.9']['ORC.19.9.3'].toString() != "") { orc[index].actionBy.assigningAuthority.universalIdType = seg['ORC.19']['ORC.19.9']['ORC.19.9.3']; }
		if (Object.keys(orc[index].actionBy.assigningAuthority).length == 0) {
			if (seg['ORC.19']['ORC.19.9'].toString() != "") { orc[index].actionBy.assigningAuthority.id = seg['ORC.19']['ORC.19.9']; }
			else delete orc[index].actionBy.assigningAuthority;
		}
		if (seg['ORC.19']['ORC.19.10'].toString() != "") { orc[index].actionBy.nameTypeCode = seg['ORC.19']['ORC.19.10']; }
		if (seg['ORC.19']['ORC.19.11'].toString() != "") { orc[index].actionBy.identifierCheckDigit = seg['ORC.19']['ORC.19.11']; }
		if (seg['ORC.19']['ORC.19.12'].toString() != "") { orc[index].actionBy.codeIdentifyingCheckDigitScheme = seg['ORC.19']['ORC.19.12']; }
		if (seg['ORC.19']['ORC.19.13'].toString() != "") { orc[index].actionBy.identifierTypeCode = seg['ORC.19']['ORC.19.13']; }
		orc[index].actionBy.assigningFacility = {};
		if (seg['ORC.19']['ORC.19.14']['ORC.19.14.1'].toString() != "") { orc[index].actionBy.assigningFacility.id = seg['ORC.19']['ORC.19.14']['ORC.19.14.1']; }
		if (seg['ORC.19']['ORC.19.14']['ORC.19.14.2'].toString() != "") { orc[index].actionBy.assigningFacility.universalId = seg['ORC.19']['ORC.19.14']['ORC.19.14.2']; }
		if (seg['ORC.19']['ORC.19.14']['ORC.19.14.3'].toString() != "") { orc[index].actionBy.assigningFacility.universalIdType = seg['ORC.19']['ORC.19.14']['ORC.19.14.3']; }
		if (Object.keys(orc[index].actionBy.assigningFacility).length == 0) {
			if (seg['ORC.19']['ORC.19.14'].toString() != "") { orc[index].actionBy.assigningFacility.id = seg['ORC.19']['ORC.19.14']; }
			else delete orc[index].actionBy.assigningFacility;
		}
		if (Object.keys(orc[index].actionBy).length == 0) {
			delete orc[index].actionBy;
		}

		// clean up ORC and increment index
		if (Object.keys(orc[index]).length == 0) {
			delete orc[index];
		} else {
			index++;
		}		
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// If it has content, add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the RXO and other segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// RXO (ORC)
		var rxoGroup = getSegmentsAfter(msg,seg,'RXO',true);
		if (rxoGroup.length > 0 && orc) {
			orc[index].RXO = [];
			var indexRXO = 0;
			for each (rxoSeg in rxoGroup) {
				orc[index].RXO[indexRXO] = {};

				// RXO.1, requestedGiveCode
				orc[index].RXO[indexRXO].requestedGiveCode = {};
				if (rxoSeg['RXO.1']['RXO.1.1'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveCode.id = rxoSeg['RXO.1']['RXO.1.1']; }
				if (rxoSeg['RXO.1']['RXO.1.2'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveCode.text = rxoSeg['RXO.1']['RXO.1.2']; }
				if (rxoSeg['RXO.1']['RXO.1.3'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveCode.codingSystem = rxoSeg['RXO.1']['RXO.1.3']; }
				if (rxoSeg['RXO.1']['RXO.1.4'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveCode.altId = rxoSeg['RXO.1']['RXO.1.4']; }
				if (rxoSeg['RXO.1']['RXO.1.5'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveCode.altText = rxoSeg['RXO.1']['RXO.1.5']; }
				if (rxoSeg['RXO.1']['RXO.1.6'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveCode.altCodingSystem = rxoSeg['RXO.1']['RXO.1.6']; }
				if (Object.keys(orc[index].RXO[indexRXO].requestedGiveCode).length == 0) {
					delete orc[index].RXO[indexRXO].requestedGiveCode;
				}

				// RXO.2, requestedGiveAmountMinimum
				if (rxoSeg['RXO.2']['RXO.2.1'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveAmountMinimum = rxoSeg['RXO.2']['RXO.2.1']; }

				// RXO.3, requestedGiveAmountMaximum
				if (rxoSeg['RXO.3']['RXO.3.1'].toString() != "") { orc[index].RXO[indexRXO].requestedGiveAmountMaximum = rxoSeg['RXO.3']['RXO.3.1']; }

				// RXO.4, requestedGiveUnits
				orc[index].RXO[indexRXO].requestedGiveUnits = {};
