							}
						}
						if (orc[index].RXO[indexRXO].NTE[indexNTE].comment.length == 0) {
							delete orc[index].RXO[indexRXO].NTE[indexNTE].comment;
						}

						// clean up NTE and increment index
						if (Object.keys(orc[index].RXO[indexRXO].NTE[indexNTE]).length == 0) {
							delete orc[index].RXO[indexRXO].NTE[indexNTE];
						} else {
							indexNTE++;
						}
					}
				}

				// RXR (RXO)
				var rxrGroup = getSegmentsAfter(msg,rxoSeg,'RXR',false, ['RXO', 'RXE', 'RXA']);
				if (rxrGroup.length > 0) {

					orc[index].RXO[indexRXO].RXR = [];
					var indexRXR = 0;
					for each (rxrSeg in rxrGroup) {
						orc[index].RXO[indexRXO].RXR[indexRXR] = {};

						// RXR.1, route
						orc[index].RXO[indexRXO].RXR[indexRXR].route = {};
						if (rxrSeg['RXR.1']['RXR.1.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].route.id = rxrSeg['RXR.1']['RXR.1.1']; }
						if (rxrSeg['RXR.1']['RXR.1.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].route.text = rxrSeg['RXR.1']['RXR.1.2']; }
						if (rxrSeg['RXR.1']['RXR.1.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].route.codingSystem = rxrSeg['RXR.1']['RXR.1.3']; }
						if (rxrSeg['RXR.1']['RXR.1.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].route.altId = rxrSeg['RXR.1']['RXR.1.4']; }
						if (rxrSeg['RXR.1']['RXR.1.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].route.altText = rxrSeg['RXR.1']['RXR.1.5']; }
						if (rxrSeg['RXR.1']['RXR.1.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].route.altCodingSystem = rxrSeg['RXR.1']['RXR.1.6']; }
						if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].route).length == 0) {
							delete orc[index].RXO[indexRXO].RXR[indexRXR].route;
						}

						// RXR.2, site
						orc[index].RXO[indexRXO].RXR[indexRXR].route = {};
						if (rxrSeg['RXR.2']['RXR.2.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].site.id = rxrSeg['RXR.2']['RXR.2.1']; }
						if (rxrSeg['RXR.2']['RXR.2.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].site.text = rxrSeg['RXR.2']['RXR.2.2']; }
						if (rxrSeg['RXR.2']['RXR.2.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].site.codingSystem = rxrSeg['RXR.2']['RXR.2.3']; }
						if (rxrSeg['RXR.2']['RXR.2.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].site.altId = rxrSeg['RXR.2']['RXR.2.4']; }
						if (rxrSeg['RXR.2']['RXR.2.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].site.altText = rxrSeg['RXR.2']['RXR.2.5']; }
						if (rxrSeg['RXR.2']['RXR.2.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].site.altCodingSystem = rxrSeg['RXR.2']['RXR.2.6']; }
						if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].site).length == 0) {
							delete orc[index].RXO[indexRXO].RXR[indexRXR].site;
						}

						// RXR.3, administrationDevice
						orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice = {};
						if (rxrSeg['RXR.3']['RXR.3.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice.id = rxrSeg['RXR.3']['RXR.3.1']; }
						if (rxrSeg['RXR.3']['RXR.3.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice.text = rxrSeg['RXR.3']['RXR.3.2']; }
						if (rxrSeg['RXR.3']['RXR.3.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice.codingSystem = rxrSeg['RXR.3']['RXR.3.3']; }
						if (rxrSeg['RXR.3']['RXR.3.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice.altId = rxrSeg['RXR.3']['RXR.3.4']; }
						if (rxrSeg['RXR.3']['RXR.3.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice.altText = rxrSeg['RXR.3']['RXR.3.5']; }
						if (rxrSeg['RXR.3']['RXR.3.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice.altCodingSystem = rxrSeg['RXR.3']['RXR.3.6']; }
						if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice).length == 0) {
							delete orc[index].RXO[indexRXO].RXR[indexRXR].administrationDevice;
						}

						// RXR.4, administrationMethod
						orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod = {};
						if (rxrSeg['RXR.4']['RXR.4.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod.id = rxrSeg['RXR.4']['RXR.4.1']; }
						if (rxrSeg['RXR.4']['RXR.4.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod.text = rxrSeg['RXR.4']['RXR.4.2']; }
						if (rxrSeg['RXR.4']['RXR.4.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod.codingSystem = rxrSeg['RXR.4']['RXR.4.3']; }
						if (rxrSeg['RXR.4']['RXR.4.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod.altId = rxrSeg['RXR.4']['RXR.4.4']; }
						if (rxrSeg['RXR.4']['RXR.4.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod.altText = rxrSeg['RXR.4']['RXR.4.5']; }
						if (rxrSeg['RXR.4']['RXR.4.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod.altCodingSystem = rxrSeg['RXR.4']['RXR.4.6']; }
						if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod).length == 0) {
							delete orc[index].RXO[indexRXO].RXR[indexRXR].administrationMethod;
						}

						// RXC (RXR)
						var rxcGroup = getSegmentsAfter(msg,rxrSeg,'RXC',true);
						if (rxcGroup.length > 0) {
							orc[index].RXO[indexRXO].RXR[indexRXR].RXC = [];
							var indexRXC = 0;
							for each (rxcSeg in rxcGroup) {
								orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC] = {};

								// RXC.1, rXComponentType
								if (rxcSeg['RXC.1']['RXC.1.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].RXComponentType = rxcSeg['RXC.1']['RXC.1.1']; }

								// RXC.2, componentCode
								orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode = {};
								if (rxcSeg['RXC.2']['RXC.2.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode.id = rxcSeg['RXC.2']['RXC.2.1']; }
								if (rxcSeg['RXC.2']['RXC.2.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode.text = rxcSeg['RXC.2']['RXC.2.2']; }
								if (rxcSeg['RXC.2']['RXC.2.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode.codingSystem = rxcSeg['RXC.2']['RXC.2.3']; }
								if (rxcSeg['RXC.2']['RXC.2.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode.altId = rxcSeg['RXC.2']['RXC.2.4']; }
								if (rxcSeg['RXC.2']['RXC.2.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode.altText = rxcSeg['RXC.2']['RXC.2.5']; }
								if (rxcSeg['RXC.2']['RXC.2.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode.altCodingSystem = rxcSeg['RXC.2']['RXC.2.6']; }
								if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode).length == 0) {
									delete orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentCode;
								}

								// RXC.3, componentAmount
								if (rxcSeg['RXC.3']['RXC.3.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentAmount = rxcSeg['RXC.3']['RXC.3.1']; }

								// RXC.4, componentUnits
								orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits = {};
								if (rxcSeg['RXC.4']['RXC.4.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits.id = rxcSeg['RXC.4']['RXC.4.1']; }
								if (rxcSeg['RXC.4']['RXC.4.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits.text = rxcSeg['RXC.4']['RXC.4.2']; }
								if (rxcSeg['RXC.4']['RXC.4.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits.codingSystem = rxcSeg['RXC.4']['RXC.4.3']; }
								if (rxcSeg['RXC.4']['RXC.4.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits.altId = rxcSeg['RXC.4']['RXC.4.4']; }
								if (rxcSeg['RXC.4']['RXC.4.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits.altText = rxcSeg['RXC.4']['RXC.4.5']; }
								if (rxcSeg['RXC.4']['RXC.4.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits.altCodingSystem = rxcSeg['RXC.4']['RXC.4.6']; }
								if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits).length == 0) {
									delete orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentUnits;
								}

								// RXC.5, componentStrength
								if (rxcSeg['RXC.5']['RXC.5.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrength = rxcSeg['RXC.5']['RXC.5.1']; }

								// RXC.6, componentStrengthUnits
								orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits = {};
								if (rxcSeg['RXC.6']['RXC.6.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits.id = rxcSeg['RXC.6']['RXC.6.1']; }
								if (rxcSeg['RXC.6']['RXC.6.2'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits.text = rxcSeg['RXC.6']['RXC.6.2']; }
								if (rxcSeg['RXC.6']['RXC.6.3'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits.codingSystem = rxcSeg['RXC.6']['RXC.6.3']; }
								if (rxcSeg['RXC.6']['RXC.6.4'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits.altId = rxcSeg['RXC.6']['RXC.6.4']; }
								if (rxcSeg['RXC.6']['RXC.6.5'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits.altText = rxcSeg['RXC.6']['RXC.6.5']; }
								if (rxcSeg['RXC.6']['RXC.6.6'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits.altCodingSystem = rxcSeg['RXC.6']['RXC.6.6']; }
								if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits).length == 0) {
									delete orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].componentStrengthUnits;
								}

								// NTE (RXC)
								var nteGroup = getSegmentsAfter(msg,rxcSeg,'NTE',false, ['RXO', 'RXE', 'RXA']);
								if (nteGroup.length > 0) {
									orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE = [];
									var indexNTE = 0;
									for each (nteSeg in nteGroup) {
										orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE] = {};

										// NTE.1, setId
										if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

										// NTE.2, sourceOfComment
										if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

										// NTE.3, comment
										orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE].comment = [];
										for each (field in nteSeg['NTE.3']) {
											var block = {};
											if (field['NTE.3.1'].toString() != "") { 
												orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE].comment.push(field['NTE.3.1']);
											}
										}
										if (orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE].comment.length == 0) {
											delete orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE].comment;
										}

										// clean up NTE and increment index
										if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE]).length == 0) {
											delete orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC].NTE[indexNTE];
										} else {
											indexNTE++;
										}
									}
								}

								// clean up RXC and increment index
								if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC]).length == 0) {
									delete orc[index].RXO[indexRXO].RXR[indexRXR].RXC[indexRXC];
								} else {
									indexRXC++;
								}
							}
						}

						// clean up RXR and incremend index
						if (Object.keys(orc[index].RXO[indexRXO].RXR[indexRXR]).length == 0) {
							delete orc[index].RXO[indexRXO].RXR[indexRXR];
						} else {
							indexRXR++;
						}
					}
				}

				// clean up RXO and increment index
				if (Object.keys(orc[index].RXO[indexRXO]).length == 0) {
					delete orc[index].RXO[indexRXO];
				} else {
					indexRXO++;
				}
			}

			// Clean up the RXO object if it's empty
			if (orc[index].RXO[0] == null){
				delete orc[index].RXO;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the RXE and other segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// RXE
		if (msg['RXE'].length() > 0) {
			orc[index].RXE = [];
			var indexRXE = 0;
			for each (rxeSeg in msg.RXE) {
				orc[index].RXE[indexRXE] = {};

				// RXE.1, timingQuantity
				orc[index].RXE[indexRXE].timingQuantity = {};
				if (rxeSeg['RXE.1']['RXE.1.1'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.quantity = rxeSeg['RXE.1']['RXE.1.1']; }
				if (rxeSeg['RXE.1']['RXE.1.2'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.interval = rxeSeg['RXE.1']['RXE.1.2']; }
				if (rxeSeg['RXE.1']['RXE.1.3'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.duration = rxeSeg['RXE.1']['RXE.1.3']; }
				if (rxeSeg['RXE.1']['RXE.1.4'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.startDateTime = rxeSeg['RXE.1']['RXE.1.4']; }
				if (rxeSeg['RXE.1']['RXE.1.5'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.endDateTime = rxeSeg['RXE.1']['RXE.1.5']; }
				if (rxeSeg['RXE.1']['RXE.1.6'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.priority = rxeSeg['RXE.1']['RXE.1.6']; }
				if (rxeSeg['RXE.1']['RXE.1.7'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.condition = rxeSeg['RXE.1']['RXE.1.7']; }
				if (rxeSeg['RXE.1']['RXE.1.8'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.text = rxeSeg['RXE.1']['RXE.1.8']; }
				if (rxeSeg['RXE.1']['RXE.1.9'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.conjunction = rxeSeg['RXE.1']['RXE.1.9']; }
				if (rxeSeg['RXE.1']['RXE.1.10'].toString() != "") { orc[index].RXE[indexRXE].timingQuantity.orderSequencing = rxeSeg['RXE.1']['RXE.1.10']; }
				if (Object.keys(orc[index].RXE[indexRXE].timingQuantity).length == 0) {
					delete orc[index].RXE[indexRXE].timingQuantity;
				}

				// RXE.2, giveCode
				orc[index].RXE[indexRXE].giveCode = {};
				if (rxeSeg['RXE.2']['RXE.2.1'].toString() != "") { orc[index].RXE[indexRXE].giveCode.id = rxeSeg['RXE.2']['RXE.2.1']; }
				if (rxeSeg['RXE.2']['RXE.2.2'].toString() != "") { orc[index].RXE[indexRXE].giveCode.text = rxeSeg['RXE.2']['RXE.2.2']; }
				if (rxeSeg['RXE.2']['RXE.2.3'].toString() != "") { orc[index].RXE[indexRXE].giveCode.codingSystem = rxeSeg['RXE.2']['RXE.2.3']; }
				if (rxeSeg['RXE.2']['RXE.2.4'].toString() != "") { orc[index].RXE[indexRXE].giveCode.altId = rxeSeg['RXE.2']['RXE.2.4']; }
				if (rxeSeg['RXE.2']['RXE.2.5'].toString() != "") { orc[index].RXE[indexRXE].giveCode.altText = rxeSeg['RXE.2']['RXE.2.5']; }
				if (rxeSeg['RXE.2']['RXE.2.6'].toString() != "") { orc[index].RXE[indexRXE].giveCode.altCodingSystem = rxeSeg['RXE.2']['RXE.2.6']; }
				if (Object.keys(orc[index].RXE[indexRXE].giveCode).length == 0) {
					delete orc[index].RXE[indexRXE].giveCode;
				}

				// RXE.3, giveAmountMinimum
				if (rxeSeg['RXE.3']['RXE.3.1'].toString() != "") { orc[index].RXE[indexRXE].giveAmountMinimum = rxeSeg['RXE.3']['RXE.3.1']; }

				// RXE.4, giveAmountMaximum
				if (rxeSeg['RXE.4']['RXE.4.1'].toString() != "") { orc[index].RXE[indexRXE].giveAmountMaximum = rxeSeg['RXE.4']['RXE.4.1']; }

				// RXE.5, giveUnits
				orc[index].RXE[indexRXE].giveUnits = {};
				if (rxeSeg['RXE.5']['RXE.5.1'].toString() != "") { orc[index].RXE[indexRXE].giveUnits.id = rxeSeg['RXE.5']['RXE.5.1']; }
				if (rxeSeg['RXE.5']['RXE.5.2'].toString() != "") { orc[index].RXE[indexRXE].giveUnits.text = rxeSeg['RXE.5']['RXE.5.2']; }
				if (rxeSeg['RXE.5']['RXE.5.3'].toString() != "") { orc[index].RXE[indexRXE].giveUnits.codingSystem = rxeSeg['RXE.5']['RXE.5.3']; }
				if (rxeSeg['RXE.5']['RXE.5.4'].toString() != "") { orc[index].RXE[indexRXE].giveUnits.altId = rxeSeg['RXE.5']['RXE.5.4']; }
				if (rxeSeg['RXE.5']['RXE.5.5'].toString() != "") { orc[index].RXE[indexRXE].giveUnits.altText = rxeSeg['RXE.5']['RXE.5.5']; }
				if (rxeSeg['RXE.5']['RXE.5.6'].toString() != "") { orc[index].RXE[indexRXE].giveUnits.altCodingSystem = rxeSeg['RXE.5']['RXE.5.6']; }
				if (Object.keys(orc[index].RXE[indexRXE].giveUnits).length == 0) {
					delete orc[index].RXE[indexRXE].giveUnits;
				}

				// RXE.6, giveDosageForm
				orc[index].RXE[indexRXE].giveDosageForm = {};
				if (rxeSeg['RXE.6']['RXE.6.1'].toString() != "") { orc[index].RXE[indexRXE].giveDosageForm.id = rxeSeg['RXE.6']['RXE.6.1']; }
