					delete orc[index].RXE[indexRXE].giveStrengthUnits;
				}

				// RXE.27, giveIndication
				orc[index].RXE[indexRXE].giveIndication = [];
				for each (field in rxeSeg['RXE.27']) {
					var block = {};
					if (field['RXE.27.1'].toString() != "") { block.id = field['RXE.27.1']; }
					if (field['RXE.27.2'].toString() != "") { block.text = field['RXE.27.2']; }
					if (field['RXE.27.3'].toString() != "") { block.codingSystem = field['RXE.27.3']; }
					if (field['RXE.27.4'].toString() != "") { block.altId = field['RXE.27.4']; }
					if (field['RXE.27.5'].toString() != "") { block.altText = field['RXE.27.5']; }
					if (field['RXE.27.6'].toString() != "") { block.altCodingSystem = field['RXE.27.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXE[indexRXE].giveIndication.push(block);
					}
				}
				if (orc[index].RXE[indexRXE].giveIndication.length == 0) {
					delete orc[index].RXE[indexRXE].giveIndication;
				}

				// RXE.28, dispensePackageSize
				if (rxeSeg['RXE.28']['RXE.28.1'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSize = rxeSeg['RXE.28']['RXE.28.1']; }

				// RXE.29, dispensePackageSizeUnit
				orc[index].RXE[indexRXE].dispensePackageSizeUnit = {};
				if (rxeSeg['RXE.29']['RXE.29.1'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSizeUnit.id = rxeSeg['RXE.29']['RXE.29.1']; }
				if (rxeSeg['RXE.29']['RXE.29.2'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSizeUnit.text = rxeSeg['RXE.29']['RXE.29.2']; }
				if (rxeSeg['RXE.29']['RXE.29.3'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSizeUnit.codingSystem = rxeSeg['RXE.29']['RXE.29.3']; }
				if (rxeSeg['RXE.29']['RXE.29.4'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSizeUnit.altId = rxeSeg['RXE.29']['RXE.29.4']; }
				if (rxeSeg['RXE.29']['RXE.29.5'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSizeUnit.altText = rxeSeg['RXE.29']['RXE.29.5']; }
				if (rxeSeg['RXE.29']['RXE.29.6'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageSizeUnit.altCodingSystem = rxeSeg['RXE.29']['RXE.29.6']; }
				if (Object.keys(orc[index].RXE[indexRXE].dispensePackageSizeUnit).length == 0) {
					delete orc[index].RXE[indexRXE].dispensePackageSizeUnit;
				}

				// RXE.30, dispensePackageMethod
				if (rxeSeg['RXE.30']['RXE.30.1'].toString() != "") { orc[index].RXE[indexRXE].dispensePackageMethod = rxeSeg['RXE.30']['RXE.30.1']; }

				// RXR (RXE)
				var rxrGroup = getSegmentsAfter(msg,rxeSeg,'RXR',false, ['RXE', 'RXA']);
				if (rxrGroup.length > 0) {

					rxrSeg = rxrGroup[0];

					orc[index].RXE[indexRXE].rxr = {};

					// RXR.1, route
					orc[index].RXE[indexRXE].rxr.route = {};
					if (rxrSeg['RXR.1']['RXR.1.1'].toString() != "") { orc[index].RXE[indexRXE].rxr.route.id = rxrSeg['RXR.1']['RXR.1.1']; }
					if (rxrSeg['RXR.1']['RXR.1.2'].toString() != "") { orc[index].RXE[indexRXE].rxr.route.text = rxrSeg['RXR.1']['RXR.1.2']; }
					if (rxrSeg['RXR.1']['RXR.1.3'].toString() != "") { orc[index].RXE[indexRXE].rxr.route.codingSystem = rxrSeg['RXR.1']['RXR.1.3']; }
					if (rxrSeg['RXR.1']['RXR.1.4'].toString() != "") { orc[index].RXE[indexRXE].rxr.route.altId = rxrSeg['RXR.1']['RXR.1.4']; }
					if (rxrSeg['RXR.1']['RXR.1.5'].toString() != "") { orc[index].RXE[indexRXE].rxr.route.altText = rxrSeg['RXR.1']['RXR.1.5']; }
					if (rxrSeg['RXR.1']['RXR.1.6'].toString() != "") { orc[index].RXE[indexRXE].rxr.route.altCodingSystem = rxrSeg['RXR.1']['RXR.1.6']; }
					if (Object.keys(orc[index].RXE[indexRXE].rxr.route).length == 0) {
						delete orc[index].RXE[indexRXE].rxr.route;
					}

					// RXR.2, site
					orc[index].RXE[indexRXE].rxr.site = {};
					if (rxrSeg['RXR.2']['RXR.2.1'].toString() != "") { orc[index].RXE[indexRXE].rxr.site.id = rxrSeg['RXR.2']['RXR.2.1']; }
					if (rxrSeg['RXR.2']['RXR.2.2'].toString() != "") { orc[index].RXE[indexRXE].rxr.site.text = rxrSeg['RXR.2']['RXR.2.2']; }
					if (rxrSeg['RXR.2']['RXR.2.3'].toString() != "") { orc[index].RXE[indexRXE].rxr.site.codingSystem = rxrSeg['RXR.2']['RXR.2.3']; }
					if (rxrSeg['RXR.2']['RXR.2.4'].toString() != "") { orc[index].RXE[indexRXE].rxr.site.altId = rxrSeg['RXR.2']['RXR.2.4']; }
					if (rxrSeg['RXR.2']['RXR.2.5'].toString() != "") { orc[index].RXE[indexRXE].rxr.site.altText = rxrSeg['RXR.2']['RXR.2.5']; }
					if (rxrSeg['RXR.2']['RXR.2.6'].toString() != "") { orc[index].RXE[indexRXE].rxr.site.altCodingSystem = rxrSeg['RXR.2']['RXR.2.6']; }
					if (Object.keys(orc[index].RXE[indexRXE].rxr.site).length == 0) {
						delete orc[index].RXE[indexRXE].rxr.site;
					}

					// RXR.3, administrationDevice
					orc[index].RXE[indexRXE].rxr.administrationDevice = {};
					if (rxrSeg['RXR.3']['RXR.3.1'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationDevice.id = rxrSeg['RXR.3']['RXR.3.1']; }
					if (rxrSeg['RXR.3']['RXR.3.2'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationDevice.text = rxrSeg['RXR.3']['RXR.3.2']; }
					if (rxrSeg['RXR.3']['RXR.3.3'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationDevice.codingSystem = rxrSeg['RXR.3']['RXR.3.3']; }
					if (rxrSeg['RXR.3']['RXR.3.4'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationDevice.altId = rxrSeg['RXR.3']['RXR.3.4']; }
					if (rxrSeg['RXR.3']['RXR.3.5'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationDevice.altText = rxrSeg['RXR.3']['RXR.3.5']; }
					if (rxrSeg['RXR.3']['RXR.3.6'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationDevice.altCodingSystem = rxrSeg['RXR.3']['RXR.3.6']; }
					if (Object.keys(orc[index].RXE[indexRXE].rxr.administrationDevice).length == 0) {
						delete orc[index].RXE[indexRXE].rxr.administrationDevice;
					}

					// RXR.4, administrationMethod
					orc[index].RXE[indexRXE].rxr.administrationMethod = {};
					if (rxrSeg['RXR.4']['RXR.4.1'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationMethod.id = rxrSeg['RXR.4']['RXR.4.1']; }
					if (rxrSeg['RXR.4']['RXR.4.2'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationMethod.text = rxrSeg['RXR.4']['RXR.4.2']; }
					if (rxrSeg['RXR.4']['RXR.4.3'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationMethod.codingSystem = rxrSeg['RXR.4']['RXR.4.3']; }
					if (rxrSeg['RXR.4']['RXR.4.4'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationMethod.altId = rxrSeg['RXR.4']['RXR.4.4']; }
					if (rxrSeg['RXR.4']['RXR.4.5'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationMethod.altText = rxrSeg['RXR.4']['RXR.4.5']; }
					if (rxrSeg['RXR.4']['RXR.4.6'].toString() != "") { orc[index].RXE[indexRXE].rxr.administrationMethod.altCodingSystem = rxrSeg['RXR.4']['RXR.4.6']; }
					if (Object.keys(orc[index].RXE[indexRXE].rxr.administrationMethod).length == 0) {
						delete orc[index].RXE[indexRXE].rxr.administrationMethod;
					}

					// clean up RXR
					if (Object.keys(orc[index].RXE[indexRXE].rxr).length == 0) {
						delete orc[index].RXE[indexRXE].rxr;
					}
				}

				// RXC (RXE)
				var rxcGroup = getSegmentsAfter(msg,rxeSeg,'RXC',true);
				if (rxcGroup.length > 0) {
					orc[index].RXE[indexRXE].RXC = [];
					var indexRXC = 0;
					for each (rxcSeg in rxcGroup) {
						orc[index].RXE[indexRXE].RXC[indexRXC] = {};

						// RXC.1, rXComponentType
						if (rxcSeg['RXC.1']['RXC.1.1'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].RXComponentType = rxcSeg['RXC.1']['RXC.1.1']; }

						// RXC.2, componentCode
						orc[index].RXE[indexRXE].RXC[indexRXC].componentCode = {};
						if (rxcSeg['RXC.2']['RXC.2.1'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentCode.id = rxcSeg['RXC.2']['RXC.2.1']; }
						if (rxcSeg['RXC.2']['RXC.2.2'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentCode.text = rxcSeg['RXC.2']['RXC.2.2']; }
						if (rxcSeg['RXC.2']['RXC.2.3'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentCode.codingSystem = rxcSeg['RXC.2']['RXC.2.3']; }
						if (rxcSeg['RXC.2']['RXC.2.4'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentCode.altId = rxcSeg['RXC.2']['RXC.2.4']; }
						if (rxcSeg['RXC.2']['RXC.2.5'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentCode.altText = rxcSeg['RXC.2']['RXC.2.5']; }
						if (rxcSeg['RXC.2']['RXC.2.6'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentCode.altCodingSystem = rxcSeg['RXC.2']['RXC.2.6']; }
						if (Object.keys(orc[index].RXE[indexRXE].RXC[indexRXC].componentCode).length == 0) {
							delete orc[index].RXE[indexRXE].RXC[indexRXC].componentCode;
						}

						// RXC.3, componentAmount
						if (rxcSeg['RXC.3']['RXC.3.1'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentAmount = rxcSeg['RXC.3']['RXC.3.1']; }

						// RXC.4, componentUnits
						orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits = {};
						if (rxcSeg['RXC.4']['RXC.4.1'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits.id = rxcSeg['RXC.4']['RXC.4.1']; }
						if (rxcSeg['RXC.4']['RXC.4.2'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits.text = rxcSeg['RXC.4']['RXC.4.2']; }
						if (rxcSeg['RXC.4']['RXC.4.3'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits.codingSystem = rxcSeg['RXC.4']['RXC.4.3']; }
						if (rxcSeg['RXC.4']['RXC.4.4'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits.altId = rxcSeg['RXC.4']['RXC.4.4']; }
						if (rxcSeg['RXC.4']['RXC.4.5'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits.altText = rxcSeg['RXC.4']['RXC.4.5']; }
						if (rxcSeg['RXC.4']['RXC.4.6'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits.altCodingSystem = rxcSeg['RXC.4']['RXC.4.6']; }
						if (Object.keys(orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits).length == 0) {
							delete orc[index].RXE[indexRXE].RXC[indexRXC].componentUnits;
						}

						// RXC.5, componentStrength
						if (rxcSeg['RXC.5']['RXC.5.1'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrength = rxcSeg['RXC.5']['RXC.5.1']; }

						// RXC.6, componentStrengthUnits
						orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits = {};
						if (rxcSeg['RXC.6']['RXC.6.1'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits.id = rxcSeg['RXC.6']['RXC.6.1']; }
						if (rxcSeg['RXC.6']['RXC.6.2'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits.text = rxcSeg['RXC.6']['RXC.6.2']; }
						if (rxcSeg['RXC.6']['RXC.6.3'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits.codingSystem = rxcSeg['RXC.6']['RXC.6.3']; }
						if (rxcSeg['RXC.6']['RXC.6.4'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits.altId = rxcSeg['RXC.6']['RXC.6.4']; }
						if (rxcSeg['RXC.6']['RXC.6.5'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits.altText = rxcSeg['RXC.6']['RXC.6.5']; }
						if (rxcSeg['RXC.6']['RXC.6.6'].toString() != "") { orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits.altCodingSystem = rxcSeg['RXC.6']['RXC.6.6']; }
						if (Object.keys(orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits).length == 0) {
							delete orc[index].RXE[indexRXE].RXC[indexRXC].componentStrengthUnits;
						}

						// clean up RXC and increment index
						if (Object.keys(orc[index].RXE[indexRXE].RXC[indexRXC]).length == 0) {
							delete orc[index].RXE[indexRXE].RXC[indexRXC];
						} else {
							indexRXC++;
						}
					}
				}

				// clean up RXE and increment index
				if (Object.keys(orc[index].RXE[indexRXE]).length == 0) {
					delete orc[index].RXE[indexRXE];
				} else {
					indexRXE++;
				}
			}

			// Clean up the RXE object if it's empty
			if (orc[index].RXE[0] == null){
				delete orc[index].RXE;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the RXO and other rxaSegments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// RXA (ORC)
		if (msg['RXA'].length() > 0) {
			orc[index].RXA = [];
			var indexRXA = 0;
			for each (rxaSeg in msg.RXA) {
				orc[index].RXA[indexRXA] = {};

				// RXA.1, giveSubIDCounter
				if (rxaSeg['RXA.1']['RXA.1.1'].toString() != "") { orc[index].RXA[indexRXA].giveSubIDCounter = rxaSeg['RXA.1']['RXA.1.1']; }

				// RXA.2, administrationSubIDCounter
				if (rxaSeg['RXA.2']['RXA.2.1'].toString() != "") { orc[index].RXA[indexRXA].administrationSubIDCounter = rxaSeg['RXA.2']['RXA.2.1']; }

				// RXA.3, dateTimeStartofAdministration
				if (rxaSeg['RXA.3']['RXA.3.1'].toString() != "") { orc[index].RXA[indexRXA].dateTimeStartofAdministration = rxaSeg['RXA.3']['RXA.3.1']; }

				// RXA.4, dateTimeEndofAdministration
				if (rxaSeg['RXA.4']['RXA.4.1'].toString() != "") { orc[index].RXA[indexRXA].dateTimeEndofAdministration = rxaSeg['RXA.4']['RXA.4.1']; }

				// RXA.5, administeredCode
				orc[index].RXA[indexRXA].administeredCode = {};
				if (rxaSeg['RXA.5']['RXA.5.1'].toString() != "") { orc[index].RXA[indexRXA].administeredCode.id = rxaSeg['RXA.5']['RXA.5.1']; }
				if (rxaSeg['RXA.5']['RXA.5.2'].toString() != "") { orc[index].RXA[indexRXA].administeredCode.text = rxaSeg['RXA.5']['RXA.5.2']; }
				if (rxaSeg['RXA.5']['RXA.5.3'].toString() != "") { orc[index].RXA[indexRXA].administeredCode.codingSystem = rxaSeg['RXA.5']['RXA.5.3']; }
				if (rxaSeg['RXA.5']['RXA.5.4'].toString() != "") { orc[index].RXA[indexRXA].administeredCode.altId = rxaSeg['RXA.5']['RXA.5.4']; }
				if (rxaSeg['RXA.5']['RXA.5.5'].toString() != "") { orc[index].RXA[indexRXA].administeredCode.altText = rxaSeg['RXA.5']['RXA.5.5']; }
				if (rxaSeg['RXA.5']['RXA.5.6'].toString() != "") { orc[index].RXA[indexRXA].administeredCode.altCodingSystem = rxaSeg['RXA.5']['RXA.5.6']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredCode).length == 0) {
					delete orc[index].RXA[indexRXA].administeredCode;
				}

				// RXA.6, administeredAmount
				if (rxaSeg['RXA.6']['RXA.6.1'].toString() != "") { orc[index].RXA[indexRXA].administeredAmount = rxaSeg['RXA.6']['RXA.6.1']; }

				// RXA.7, administeredUnits
				orc[index].RXA[indexRXA].administeredUnits = {};
				if (rxaSeg['RXA.7']['RXA.7.1'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.id = rxaSeg['RXA.7']['RXA.7.1']; }
				if (rxaSeg['RXA.7']['RXA.7.2'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.text = rxaSeg['RXA.7']['RXA.7.2']; }
				if (rxaSeg['RXA.7']['RXA.7.3'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.codingSystem = rxaSeg['RXA.7']['RXA.7.3']; }
				if (rxaSeg['RXA.7']['RXA.7.4'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.altId = rxaSeg['RXA.7']['RXA.7.4']; }
				if (rxaSeg['RXA.7']['RXA.7.5'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.altText = rxaSeg['RXA.7']['RXA.7.5']; }
				if (rxaSeg['RXA.7']['RXA.7.6'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.altCodingSystem = rxaSeg['RXA.7']['RXA.7.6']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredUnits).length == 0) {
					delete orc[index].RXA[indexRXA].administeredUnits;
				}

				// RXA.8, administeredDosageForm
				orc[index].RXA[indexRXA].administeredDosageForm = {};
				if (rxaSeg['RXA.8']['RXA.8.1'].toString() != "") { orc[index].RXA[indexRXA].administeredDosageForm.id = rxaSeg['RXA.8']['RXA.8.1']; }
				if (rxaSeg['RXA.8']['RXA.8.2'].toString() != "") { orc[index].RXA[indexRXA].administeredDosageForm.text = rxaSeg['RXA.8']['RXA.8.2']; }
				if (rxaSeg['RXA.8']['RXA.8.3'].toString() != "") { orc[index].RXA[indexRXA].administeredDosageForm.codingSystem = rxaSeg['RXA.8']['RXA.8.3']; }
				if (rxaSeg['RXA.8']['RXA.8.4'].toString() != "") { orc[index].RXA[indexRXA].administeredDosageForm.altId = rxaSeg['RXA.8']['RXA.8.4']; }
				if (rxaSeg['RXA.8']['RXA.8.5'].toString() != "") { orc[index].RXA[indexRXA].administeredDosageForm.altText = rxaSeg['RXA.8']['RXA.8.5']; }
				if (rxaSeg['RXA.8']['RXA.8.6'].toString() != "") { orc[index].RXA[indexRXA].administeredDosageForm.altCodingSystem = rxaSeg['RXA.8']['RXA.8.6']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredDosageForm).length == 0) {
					delete orc[index].RXA[indexRXA].administeredDosageForm;
				}

				// RXA.9, administrationNotes
				orc[index].RXA[indexRXA].administrationNotes = [];
				for each (field in rxaSeg['RXA.9']) {
					var block = {};
					if (field['RXA.9.1'].toString() != "") { block.id = field['RXA.9.1']; }
					if (field['RXA.9.2'].toString() != "") { block.text = field['RXA.9.2']; }
					if (field['RXA.9.3'].toString() != "") { block.codingSystem = field['RXA.9.3']; }
					if (field['RXA.9.4'].toString() != "") { block.altId = field['RXA.9.4']; }
					if (field['RXA.9.5'].toString() != "") { block.altText = field['RXA.9.5']; }
					if (field['RXA.9.6'].toString() != "") { block.altCodingSystem = field['RXA.9.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXA[indexRXA].administrationNotes.push(block);
					}
				}
				if (orc[index].RXA[indexRXA].administrationNotes.length == 0) {
					delete orc[index].RXA[indexRXA].administrationNotes;
				}

				// RXA.10, administeringProvider
				orc[index].RXA[indexRXA].administeringProvider = {};
				if (rxaSeg['RXA.10']['RXA.10.1'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.id = rxaSeg['RXA.10']['RXA.10.1']; }
				if (rxaSeg['RXA.10']['RXA.10.2'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.lastName = rxaSeg['RXA.10']['RXA.10.2']; }
