				if (Object.keys(orc[index].RXA[indexRXA].administeringProvider.assigningAuthority).length == 0) {
					if (rxaSeg['RXA.10']['RXA.10.9'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningAuthority.id = rxaSeg['RXA.10']['RXA.10.9']; }
					else delete orc[index].RXA[indexRXA].administeringProvider.assigningAuthority;
				}
				if (rxaSeg['RXA.10']['RXA.10.10'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.nameTypeCode = rxaSeg['RXA.10']['RXA.10.10']; }
				if (rxaSeg['RXA.10']['RXA.10.11'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.identifierCheckDigit = rxaSeg['RXA.10']['RXA.10.11']; }
				if (rxaSeg['RXA.10']['RXA.10.12'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.codeIdentifyingCheckDigitScheme = rxaSeg['RXA.10']['RXA.10.12']; }
				if (rxaSeg['RXA.10']['RXA.10.13'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.identifierTypeCode = rxaSeg['RXA.10']['RXA.10.13']; }
				orc[index].RXA[indexRXA].administeringProvider.assigningFacility = {};
				if (rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.1'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.id = rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.1']; }
				if (rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.2'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.universalId = rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.2']; }
				if (rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.3'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.universalIdType = rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.3']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeringProvider.assigningFacility).length == 0) {
					if (rxaSeg['RXA.10']['RXA.10.14'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.id = rxaSeg['RXA.10']['RXA.10.14']; }
					else delete orc[index].RXA[indexRXA].administeringProvider.assigningFacility;
				}
				if (Object.keys(orc[index].RXA[indexRXA].administeringProvider).length == 0) {
