				if (rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.1'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.nameSpaceId =  rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.1']; }
				if (rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.2'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.universalId =  rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.2']; }
