					if (rxaSeg['RXA.11']['RXA.11.4'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.nameSpaceId = rxaSeg['RXA.11']['RXA.11.4']; }
					else delete orc[index].RXA[indexRXA].administeredAtLocation.facility;
				}
				if (rxaSeg['RXA.11']['RXA.11.5'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.locationStatus = rxaSeg['RXA.11']['RXA.11.5']; }
				if (rxaSeg['RXA.11']['RXA.11.6'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.personLocationType = rxaSeg['RXA.11']['RXA.11.6']; }
				if (rxaSeg['RXA.11']['RXA.11.7'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.building = rxaSeg['RXA.11']['RXA.11.7']; }
				if (rxaSeg['RXA.11']['RXA.11.8'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.floor = rxaSeg['RXA.11']['RXA.11.8']; }
				if (rxaSeg['RXA.11']['RXA.11.9'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.locationDescription = rxaSeg['RXA.11']['RXA.11.9']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredAtLocation).length == 0) {
					delete orc[index].RXA[indexRXA].administeredAtLocation;
				}

				// RXA.12, administeredPerTimeUnit
				if (rxaSeg['RXA.12']['RXA.12.1'].toString() != "") { orc[index].RXA[indexRXA].administeredPerTimeUnit = rxaSeg['RXA.12']['RXA.12.1']; }

				// RXA.13, administeredStrength
				if (rxaSeg['RXA.13']['RXA.13.1'].toString() != "") { orc[index].RXA[indexRXA].administeredStrength = rxaSeg['RXA.13']['RXA.13.1']; }

