				orc[index].RXA[indexRXA].substanceLotNumber = [];
				for each (field in rxaSeg['RXA.15']) {
					if (field['RXA.15.1'].toString() != "") {
						orc[index].RXA[indexRXA].substanceLotNumber.push(field['RXA.15.1']);
