				for each (field in rxaSeg['RXA.18']) {
					var block = {};
					if (field['RXA.18.1'].toString() != "") { block.id = field['RXA.18.1']; }
					if (field['RXA.18.2'].toString() != "") { block.text = field['RXA.18.2']; }
					if (field['RXA.18.3'].toString() != "") { block.codingSystem = field['RXA.18.3']; }
					if (field['RXA.18.4'].toString() != "") { block.altId = field['RXA.18.4']; }
					if (field['RXA.18.5'].toString() != "") { block.altText = field['RXA.18.5']; }
					if (field['RXA.18.6'].toString() != "") { block.altCodingSystem = field['RXA.18.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXA[indexRXA].substanceRefusalReason.push(block);
					}
				}
				if (orc[index].RXA[indexRXA].substanceRefusalReason.length == 0) {
					delete orc[index].RXA[indexRXA].substanceRefusalReason;
				}

				// RXA.19, indication
				orc[index].RXA[indexRXA].indication = [];
				for each (field in rxaSeg['RXA.19']) {
					var block = {};
					if (field['RXA.19.1'].toString() != "") { block.id = field['RXA.19.1']; }
					if (field['RXA.19.2'].toString() != "") { block.text = field['RXA.19.2']; }
					if (field['RXA.19.3'].toString() != "") { block.codingSystem = field['RXA.19.3']; }
					if (field['RXA.19.4'].toString() != "") { block.altId = field['RXA.19.4']; }
					if (field['RXA.19.5'].toString() != "") { block.altText = field['RXA.19.5']; }
					if (field['RXA.19.6'].toString() != "") { block.altCodingSystem = field['RXA.19.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXA[indexRXA].indication.push(block);
					}
				}
				if (orc[index].RXA[indexRXA].indication.length == 0) {
					delete orc[index].RXA[indexRXA].indication;
				}

				// RXA.20, completionStatus
				if (rxaSeg['RXA.20']['RXA.20.1'].toString() != "") { orc[index].RXA[indexRXA].completionStatus = rxaSeg['RXA.20']['RXA.20.1']; }

				// RXA.21, actionCode
				if (rxaSeg['RXA.21']['RXA.21.1'].toString() != "") { orc[index].RXA[indexRXA].actionCode = rxaSeg['RXA.21']['RXA.21.1']; }

				// RXA.22, systemEntryDateTime
				if (rxaSeg['RXA.22']['RXA.22.1'].toString() != "") { orc[index].RXA[indexRXA].systemEntryDateTime = rxaSeg['RXA.22']['RXA.22.1']; }

				// clean up RXA and increment indexRXA
				if (Object.keys(orc[index].RXA[indexRXA]).length == 0) {
					delete orc[index].RXA[indexRXA];
				} else {
					indexRXA++;
				}
			}

			// Clean up the RXA object if it's empty
			if (orc[index].RXA[0] == null){
				delete orc[index].RXA;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

entry.string:
// Pull out the ORC JSON to embed the RXO and other rxaSegments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// RXA (ORC)
		if (msg['RXA'].length() > 0) {
			orc[index].RXA = [];
			var indexRXA = 0;
			for each (rxaSeg in msg.RXA) {
				orc[index].RXA[indexRXA] = {};

				// RXA.1, giveSubIDCounter
				if (rxaSeg['RXA.1']['RXA.1.1'].toString() != "") { orc[index].RXA[indexRXA].giveSubIDCounter = rxaSeg['RXA.1']['RXA.1.1']; }

				// RXA.2, administrationSubIDCounter
				if (rxaSeg['RXA.2']['RXA.2.1'].toString() != "") { orc[index].RXA[indexRXA].administrationSubIDCounter = rxaSeg['RXA.2']['RXA.2.1']; }

				// RXA.3, dateTimeStartofAdministration
				if (rxaSeg['RXA.3']['RXA.3.1'].toString() != "") { orc[index].RXA[indexRXA].dateTimeStartofAdministration = rxaSeg['RXA.3']['RXA.3.1']; }

				// RXA.4, dateTimeEndofAdministration
