				if (rxaSeg['RXA.7']['RXA.7.3'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.codingSystem = rxaSeg['RXA.7']['RXA.7.3']; }
				if (rxaSeg['RXA.7']['RXA.7.4'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.altId = rxaSeg['RXA.7']['RXA.7.4']; }
				if (rxaSeg['RXA.7']['RXA.7.5'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.altText = rxaSeg['RXA.7']['RXA.7.5']; }
				if (rxaSeg['RXA.7']['RXA.7.6'].toString() != "") { orc[index].RXA[indexRXA].administeredUnits.altCodingSystem = rxaSeg['RXA.7']['RXA.7.6']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredUnits).length == 0) {
					delete orc[index].RXA[indexRXA].administeredUnits;
				}

				// RXA.8, administeredDosageForm
				orc[index].RXA[indexRXA].administeredDosageForm = {};
