				if (rxaSeg['RXA.10']['RXA.10.4'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.middleInitOrName = rxaSeg['RXA.10']['RXA.10.4']; }
				if (rxaSeg['RXA.10']['RXA.10.5'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.suffix = rxaSeg['RXA.10']['RXA.10.5']; }
				if (rxaSeg['RXA.10']['RXA.10.6'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.prefix = rxaSeg['RXA.10']['RXA.10.6']; }
				if (rxaSeg['RXA.10']['RXA.10.7'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.degree = rxaSeg['RXA.10']['RXA.10.7']; }
				if (rxaSeg['RXA.10']['RXA.10.8'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.sourceTable = rxaSeg['RXA.10']['RXA.10.8']; }
				orc[index].RXA[indexRXA].administeringProvider.assigningAuthority = {};
				if (rxaSeg['RXA.10']['RXA.10.9']['RXA.10.9.1'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningAuthority.id = rxaSeg['RXA.10']['RXA.10.9']['RXA.10.9.1']; }
				if (rxaSeg['RXA.10']['RXA.10.9']['RXA.10.9.2'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningAuthority.universalId = rxaSeg['RXA.10']['RXA.10.9']['RXA.10.9.2']; }
				if (rxaSeg['RXA.10']['RXA.10.9']['RXA.10.9.3'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningAuthority.universalIdType = rxaSeg['RXA.10']['RXA.10.9']['RXA.10.9.3']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeringProvider.assigningAuthority).length == 0) {
					if (rxaSeg['RXA.10']['RXA.10.9'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningAuthority.id = rxaSeg['RXA.10']['RXA.10.9']; }
					else delete orc[index].RXA[indexRXA].administeringProvider.assigningAuthority;
				}
				if (rxaSeg['RXA.10']['RXA.10.10'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.nameTypeCode = rxaSeg['RXA.10']['RXA.10.10']; }
				if (rxaSeg['RXA.10']['RXA.10.11'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.identifierCheckDigit = rxaSeg['RXA.10']['RXA.10.11']; }
				if (rxaSeg['RXA.10']['RXA.10.12'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.codeIdentifyingCheckDigitScheme = rxaSeg['RXA.10']['RXA.10.12']; }
				if (rxaSeg['RXA.10']['RXA.10.13'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.identifierTypeCode = rxaSeg['RXA.10']['RXA.10.13']; }
				orc[index].RXA[indexRXA].administeringProvider.assigningFacility = {};
				if (rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.1'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.id = rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.1']; }
				if (rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.2'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.universalId = rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.2']; }
				if (rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.3'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.universalIdType = rxaSeg['RXA.10']['RXA.10.14']['RXA.10.14.3']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeringProvider.assigningFacility).length == 0) {
					if (rxaSeg['RXA.10']['RXA.10.14'].toString() != "") { orc[index].RXA[indexRXA].administeringProvider.assigningFacility.id = rxaSeg['RXA.10']['RXA.10.14']; }
					else delete orc[index].RXA[indexRXA].administeringProvider.assigningFacility;
				}
				if (Object.keys(orc[index].RXA[indexRXA].administeringProvider).length == 0) {
					delete orc[index].RXA[indexRXA].administeringProvider;
				}

				// RXA.11, administeredAtLocation
				orc[index].RXA[indexRXA].administeredAtLocation = {};
				if (rxaSeg['RXA.11']['RXA.11.1'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.pointOfCare = rxaSeg['RXA.11']['RXA.11.1']; }
				if (rxaSeg['RXA.11']['RXA.11.2'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.room = rxaSeg['RXA.11']['RXA.11.2']; }
				if (rxaSeg['RXA.11']['RXA.11.3'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.bed = rxaSeg['RXA.11']['RXA.11.3']; }
				orc[index].RXA[indexRXA].administeredAtLocation.facility = {};
				if (rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.1'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.nameSpaceId =  rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.1']; }
				if (rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.2'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.universalId =  rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.2']; }
				if (rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.3'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.universalIdType =  rxaSeg['RXA.11']['RXA.11.4']['RXA.11.4.3']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredAtLocation.facility).length == 0) {
					if (rxaSeg['RXA.11']['RXA.11.4'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.facility.nameSpaceId = rxaSeg['RXA.11']['RXA.11.4']; }
					else delete orc[index].RXA[indexRXA].administeredAtLocation.facility;
				}
				if (rxaSeg['RXA.11']['RXA.11.5'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.locationStatus = rxaSeg['RXA.11']['RXA.11.5']; }
				if (rxaSeg['RXA.11']['RXA.11.6'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.personLocationType = rxaSeg['RXA.11']['RXA.11.6']; }
				if (rxaSeg['RXA.11']['RXA.11.7'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.building = rxaSeg['RXA.11']['RXA.11.7']; }
				if (rxaSeg['RXA.11']['RXA.11.8'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.floor = rxaSeg['RXA.11']['RXA.11.8']; }
				if (rxaSeg['RXA.11']['RXA.11.9'].toString() != "") { orc[index].RXA[indexRXA].administeredAtLocation.locationDescription = rxaSeg['RXA.11']['RXA.11.9']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredAtLocation).length == 0) {
					delete orc[index].RXA[indexRXA].administeredAtLocation;
				}

				// RXA.12, administeredPerTimeUnit
				if (rxaSeg['RXA.12']['RXA.12.1'].toString() != "") { orc[index].RXA[indexRXA].administeredPerTimeUnit = rxaSeg['RXA.12']['RXA.12.1']; }

				// RXA.13, administeredStrength
				if (rxaSeg['RXA.13']['RXA.13.1'].toString() != "") { orc[index].RXA[indexRXA].administeredStrength = rxaSeg['RXA.13']['RXA.13.1']; }

				// RXA.14, administeredStrengthUnits
				orc[index].RXA[indexRXA].administeredStrengthUnits = {};
				if (rxaSeg['RXA.14']['RXA.14.1'].toString() != "") { orc[index].RXA[indexRXA].administeredStrengthUnits.id = rxaSeg['RXA.14']['RXA.14.1']; }
				if (rxaSeg['RXA.14']['RXA.14.2'].toString() != "") { orc[index].RXA[indexRXA].administeredStrengthUnits.text = rxaSeg['RXA.14']['RXA.14.2']; }
				if (rxaSeg['RXA.14']['RXA.14.3'].toString() != "") { orc[index].RXA[indexRXA].administeredStrengthUnits.codingSystem = rxaSeg['RXA.14']['RXA.14.3']; }
				if (rxaSeg['RXA.14']['RXA.14.4'].toString() != "") { orc[index].RXA[indexRXA].administeredStrengthUnits.altId = rxaSeg['RXA.14']['RXA.14.4']; }
				if (rxaSeg['RXA.14']['RXA.14.5'].toString() != "") { orc[index].RXA[indexRXA].administeredStrengthUnits.altText = rxaSeg['RXA.14']['RXA.14.5']; }
				if (rxaSeg['RXA.14']['RXA.14.6'].toString() != "") { orc[index].RXA[indexRXA].administeredStrengthUnits.altCodingSystem = rxaSeg['RXA.14']['RXA.14.6']; }
				if (Object.keys(orc[index].RXA[indexRXA].administeredStrengthUnits).length == 0) {
					delete orc[index].RXA[indexRXA].administeredStrengthUnits;
				}

				// RXA.15, substanceLotNumber
				orc[index].RXA[indexRXA].substanceLotNumber = [];
				for each (field in rxaSeg['RXA.15']) {
					if (field['RXA.15.1'].toString() != "") {
						orc[index].RXA[indexRXA].substanceLotNumber.push(field['RXA.15.1']);
					}
				}
				if (Object.keys(orc[index].RXA[indexRXA].substanceLotNumber).length == 0) {
					delete orc[index].RXA[indexRXA].substanceLotNumber;
				}

				// RXA.16, substanceExpirationDate
				orc[index].RXA[indexRXA].substanceExpirationDate = [];
				for each (field in rxaSeg['RXA.16']) {
					if (field['RXA.16.1'].toString() != "") {
						orc[index].RXA[indexRXA].substanceExpirationDate.push(field['RXA.16.1']);
					}
				}
				if (orc[index].RXA[indexRXA].substanceExpirationDate.length == 0) {
					delete orc[index].RXA[indexRXA].substanceExpirationDate;
				}

				// RXA.17, substanceManufacturerName
				orc[index].RXA[indexRXA].substanceManufacturerName = [];
				for each (field in rxaSeg['RXA.17']) {
					var block = {};
					if (field['RXA.17.1'].toString() != "") { block.id = field['RXA.17.1']; }
					if (field['RXA.17.2'].toString() != "") { block.text = field['RXA.17.2']; }
					if (field['RXA.17.3'].toString() != "") { block.codingSystem = field['RXA.17.3']; }
					if (field['RXA.17.4'].toString() != "") { block.altId = field['RXA.17.4']; }
					if (field['RXA.17.5'].toString() != "") { block.altText = field['RXA.17.5']; }
					if (field['RXA.17.6'].toString() != "") { block.altCodingSystem = field['RXA.17.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXA[indexRXA].substanceManufacturerName.push(block);
					}
				}
				if (orc[index].RXA[indexRXA].substanceManufacturerName.length == 0) {
					delete orc[index].RXA[indexRXA].substanceManufacturerName;
				}

				// RXA.18, substanceRefusalReason
				orc[index].RXA[indexRXA].substanceRefusalReason = [];
				for each (field in rxaSeg['RXA.18']) {
					var block = {};
					if (field['RXA.18.1'].toString() != "") { block.id = field['RXA.18.1']; }
					if (field['RXA.18.2'].toString() != "") { block.text = field['RXA.18.2']; }
					if (field['RXA.18.3'].toString() != "") { block.codingSystem = field['RXA.18.3']; }
					if (field['RXA.18.4'].toString() != "") { block.altId = field['RXA.18.4']; }
					if (field['RXA.18.5'].toString() != "") { block.altText = field['RXA.18.5']; }
					if (field['RXA.18.6'].toString() != "") { block.altCodingSystem = field['RXA.18.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXA[indexRXA].substanceRefusalReason.push(block);
					}
				}
				if (orc[index].RXA[indexRXA].substanceRefusalReason.length == 0) {
					delete orc[index].RXA[indexRXA].substanceRefusalReason;
				}

				// RXA.19, indication
				orc[index].RXA[indexRXA].indication = [];
				for each (field in rxaSeg['RXA.19']) {
					var block = {};
					if (field['RXA.19.1'].toString() != "") { block.id = field['RXA.19.1']; }
					if (field['RXA.19.2'].toString() != "") { block.text = field['RXA.19.2']; }
					if (field['RXA.19.3'].toString() != "") { block.codingSystem = field['RXA.19.3']; }
					if (field['RXA.19.4'].toString() != "") { block.altId = field['RXA.19.4']; }
					if (field['RXA.19.5'].toString() != "") { block.altText = field['RXA.19.5']; }
					if (field['RXA.19.6'].toString() != "") { block.altCodingSystem = field['RXA.19.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].RXA[indexRXA].indication.push(block);
					}
				}
				if (orc[index].RXA[indexRXA].indication.length == 0) {
					delete orc[index].RXA[indexRXA].indication;
				}

				// RXA.20, completionStatus
				if (rxaSeg['RXA.20']['RXA.20.1'].toString() != "") { orc[index].RXA[indexRXA].completionStatus = rxaSeg['RXA.20']['RXA.20.1']; }

				// RXA.21, actionCode
				if (rxaSeg['RXA.21']['RXA.21.1'].toString() != "") { orc[index].RXA[indexRXA].actionCode = rxaSeg['RXA.21']['RXA.21.1']; }

				// RXA.22, systemEntryDateTime
				if (rxaSeg['RXA.22']['RXA.22.1'].toString() != "") { orc[index].RXA[indexRXA].systemEntryDateTime = rxaSeg['RXA.22']['RXA.22.1']; }

				// clean up RXA and increment indexRXA
				if (Object.keys(orc[index].RXA[indexRXA]).length == 0) {
					delete orc[index].RXA[indexRXA];
				} else {
					indexRXA++;
				}
			}

			// Clean up the RXA object if it's empty
			if (orc[index].RXA[0] == null){
				delete orc[index].RXA;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the RXO and other rxaSegments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// RXR
		orc[index].RXR = {};

		if (msg['RXA'].length() > 0) {

			// Get the last RXA segment, since we'll want to get the RXR after that one
			var lastRXAseg = msg['RXA'][msg['RXA'].length() - 1];
			var rxrGroup = getSegmentsAfter(msg,lastRXAseg,'RXR',true); // TODO: need to see if this works

			if (rxrGroup.length > 0) {

				var rxrSeg = rxrGroup[0];

				// orc[index].RXR.1, route
				orc[index].RXR.route = {};
				if (rxrSeg['RXR.1']['RXR.1.1'].toString() != "") { orc[index].RXR.route.id = rxrSeg['RXR.1']['RXR.1.1']; }
				if (rxrSeg['RXR.1']['RXR.1.2'].toString() != "") { orc[index].RXR.route.text = rxrSeg['RXR.1']['RXR.1.2']; }
				if (rxrSeg['RXR.1']['RXR.1.3'].toString() != "") { orc[index].RXR.route.codingSystem = rxrSeg['RXR.1']['RXR.1.3']; }
				if (rxrSeg['RXR.1']['RXR.1.4'].toString() != "") { orc[index].RXR.route.altId = rxrSeg['RXR.1']['RXR.1.4']; }
				if (rxrSeg['RXR.1']['RXR.1.5'].toString() != "") { orc[index].RXR.route.altText = rxrSeg['RXR.1']['RXR.1.5']; }
				if (rxrSeg['RXR.1']['RXR.1.6'].toString() != "") { orc[index].RXR.route.altCodingSystem = rxrSeg['RXR.1']['RXR.1.6']; }
				if (Object.keys(orc[index].RXR.route).length == 0) {
					delete orc[index].RXR.route;
				}

				// orc[index].RXR.2, site
				orc[index].RXR.site = {};
				if (rxrSeg['RXR.2']['RXR.2.1'].toString() != "") { orc[index].RXR.site.id = rxrSeg['RXR.2']['RXR.2.1']; }
				if (rxrSeg['RXR.2']['RXR.2.2'].toString() != "") { orc[index].RXR.site.text = rxrSeg['RXR.2']['RXR.2.2']; }
				if (rxrSeg['RXR.2']['RXR.2.3'].toString() != "") { orc[index].RXR.site.codingSystem = rxrSeg['RXR.2']['RXR.2.3']; }
				if (rxrSeg['RXR.2']['RXR.2.4'].toString() != "") { orc[index].RXR.site.altId = rxrSeg['RXR.2']['RXR.2.4']; }
				if (rxrSeg['RXR.2']['RXR.2.5'].toString() != "") { orc[index].RXR.site.altText = rxrSeg['RXR.2']['RXR.2.5']; }
				if (rxrSeg['RXR.2']['RXR.2.6'].toString() != "") { orc[index].RXR.site.altCodingSystem = rxrSeg['RXR.2']['RXR.2.6']; }
				if (Object.keys(orc[index].RXR.site).length == 0) {
					delete orc[index].RXR.site;
				}

				// orc[index].RXR.3, administrationDevice
				orc[index].RXR.administrationDevice = {};
				if (rxrSeg['RXR.3']['RXR.3.1'].toString() != "") { orc[index].RXR.administrationDevice.id = rxrSeg['RXR.3']['RXR.3.1']; }
				if (rxrSeg['RXR.3']['RXR.3.2'].toString() != "") { orc[index].RXR.administrationDevice.text = rxrSeg['RXR.3']['RXR.3.2']; }
				if (rxrSeg['RXR.3']['RXR.3.3'].toString() != "") { orc[index].RXR.administrationDevice.codingSystem = rxrSeg['RXR.3']['RXR.3.3']; }
				if (rxrSeg['RXR.3']['RXR.3.4'].toString() != "") { orc[index].RXR.administrationDevice.altId = rxrSeg['RXR.3']['RXR.3.4']; }
				if (rxrSeg['RXR.3']['RXR.3.5'].toString() != "") { orc[index].RXR.administrationDevice.altText = rxrSeg['RXR.3']['RXR.3.5']; }
				if (rxrSeg['RXR.3']['RXR.3.6'].toString() != "") { orc[index].RXR.administrationDevice.altCodingSystem = rxrSeg['RXR.3']['RXR.3.6']; }
				if (Object.keys(orc[index].RXR.administrationDevice).length == 0) {
					delete orc[index].RXR.administrationDevice;
				}

				// orc[index].RXR.4, administrationMethod
				orc[index].RXR.administrationMethod = {};
				if (rxrSeg['RXR.4']['RXR.4.1'].toString() != "") { orc[index].RXR.administrationMethod.id = rxrSeg['RXR.4']['RXR.4.1']; }
				if (rxrSeg['RXR.4']['RXR.4.2'].toString() != "") { orc[index].RXR.administrationMethod.text = rxrSeg['RXR.4']['RXR.4.2']; }
				if (rxrSeg['RXR.4']['RXR.4.3'].toString() != "") { orc[index].RXR.administrationMethod.codingSystem = rxrSeg['RXR.4']['RXR.4.3']; }
				if (rxrSeg['RXR.4']['RXR.4.4'].toString() != "") { orc[index].RXR.administrationMethod.altId = rxrSeg['RXR.4']['RXR.4.4']; }
				if (rxrSeg['RXR.4']['RXR.4.5'].toString() != "") { orc[index].RXR.administrationMethod.altText = rxrSeg['RXR.4']['RXR.4.5']; }
				if (rxrSeg['RXR.4']['RXR.4.6'].toString() != "") { orc[index].RXR.administrationMethod.altCodingSystem = rxrSeg['RXR.4']['RXR.4.6']; }
				if (Object.keys(orc[index].RXR.administrationMethod).length == 0) {
					delete orc[index].RXR.administrationMethod;
				}
			}
		}

		// clean up RXR
		if (Object.keys(orc[index].RXR).length == 0) {
			delete orc[index].RXR;
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

entry.string:
// Pull out the ORC JSON to embed the RXO and other rxaSegments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// RXR
		orc[index].RXR = {};

		if (msg['RXA'].length() > 0) {

			// Get the last RXA segment, since we'll want to get the RXR after that one
			var lastRXAseg = msg['RXA'][msg['RXA'].length() - 1];
			var rxrGroup = getSegmentsAfter(msg,lastRXAseg,'RXR',true); // TODO: need to see if this works

			if (rxrGroup.length > 0) {

				var rxrSeg = rxrGroup[0];

				// orc[index].RXR.1, route
				orc[index].RXR.route = {};
				if (rxrSeg['RXR.1']['RXR.1.1'].toString() != "") { orc[index].RXR.route.id = rxrSeg['RXR.1']['RXR.1.1']; }
				if (rxrSeg['RXR.1']['RXR.1.2'].toString() != "") { orc[index].RXR.route.text = rxrSeg['RXR.1']['RXR.1.2']; }
				if (rxrSeg['RXR.1']['RXR.1.3'].toString() != "") { orc[index].RXR.route.codingSystem = rxrSeg['RXR.1']['RXR.1.3']; }
				if (rxrSeg['RXR.1']['RXR.1.4'].toString() != "") { orc[index].RXR.route.altId = rxrSeg['RXR.1']['RXR.1.4']; }
				if (rxrSeg['RXR.1']['RXR.1.5'].toString() != "") { orc[index].RXR.route.altText = rxrSeg['RXR.1']['RXR.1.5']; }
				if (rxrSeg['RXR.1']['RXR.1.6'].toString() != "") { orc[index].RXR.route.altCodingSystem = rxrSeg['RXR.1']['RXR.1.6']; }
				if (Object.keys(orc[index].RXR.route).length == 0) {
					delete orc[index].RXR.route;
				}

				// orc[index].RXR.2, site
				orc[index].RXR.site = {};
				if (rxrSeg['RXR.2']['RXR.2.1'].toString() != "") { orc[index].RXR.site.id = rxrSeg['RXR.2']['RXR.2.1']; }
				if (rxrSeg['RXR.2']['RXR.2.2'].toString() != "") { orc[index].RXR.site.text = rxrSeg['RXR.2']['RXR.2.2']; }
				if (rxrSeg['RXR.2']['RXR.2.3'].toString() != "") { orc[index].RXR.site.codingSystem = rxrSeg['RXR.2']['RXR.2.3']; }
				if (rxrSeg['RXR.2']['RXR.2.4'].toString() != "") { orc[index].RXR.site.altId = rxrSeg['RXR.2']['RXR.2.4']; }
				if (rxrSeg['RXR.2']['RXR.2.5'].toString() != "") { orc[index].RXR.site.altText = rxrSeg['RXR.2']['RXR.2.5']; }
				if (rxrSeg['RXR.2']['RXR.2.6'].toString() != "") { orc[index].RXR.site.altCodingSystem = rxrSeg['RXR.2']['RXR.2.6']; }
				if (Object.keys(orc[index].RXR.site).length == 0) {
					delete orc[index].RXR.site;
				}

				// orc[index].RXR.3, administrationDevice
				orc[index].RXR.administrationDevice = {};
				if (rxrSeg['RXR.3']['RXR.3.1'].toString() != "") { orc[index].RXR.administrationDevice.id = rxrSeg['RXR.3']['RXR.3.1']; }
				if (rxrSeg['RXR.3']['RXR.3.2'].toString() != "") { orc[index].RXR.administrationDevice.text = rxrSeg['RXR.3']['RXR.3.2']; }
				if (rxrSeg['RXR.3']['RXR.3.3'].toString() != "") { orc[index].RXR.administrationDevice.codingSystem = rxrSeg['RXR.3']['RXR.3.3']; }
				if (rxrSeg['RXR.3']['RXR.3.4'].toString() != "") { orc[index].RXR.administrationDevice.altId = rxrSeg['RXR.3']['RXR.3.4']; }
				if (rxrSeg['RXR.3']['RXR.3.5'].toString() != "") { orc[index].RXR.administrationDevice.altText = rxrSeg['RXR.3']['RXR.3.5']; }
				if (rxrSeg['RXR.3']['RXR.3.6'].toString() != "") { orc[index].RXR.administrationDevice.altCodingSystem = rxrSeg['RXR.3']['RXR.3.6']; }
				if (Object.keys(orc[index].RXR.administrationDevice).length == 0) {
					delete orc[index].RXR.administrationDevice;
				}

				// orc[index].RXR.4, administrationMethod
				orc[index].RXR.administrationMethod = {};
				if (rxrSeg['RXR.4']['RXR.4.1'].toString() != "") { orc[index].RXR.administrationMethod.id = rxrSeg['RXR.4']['RXR.4.1']; }
				if (rxrSeg['RXR.4']['RXR.4.2'].toString() != "") { orc[index].RXR.administrationMethod.text = rxrSeg['RXR.4']['RXR.4.2']; }
				if (rxrSeg['RXR.4']['RXR.4.3'].toString() != "") { orc[index].RXR.administrationMethod.codingSystem = rxrSeg['RXR.4']['RXR.4.3']; }
				if (rxrSeg['RXR.4']['RXR.4.4'].toString() != "") { orc[index].RXR.administrationMethod.altId = rxrSeg['RXR.4']['RXR.4.4']; }
				if (rxrSeg['RXR.4']['RXR.4.5'].toString() != "") { orc[index].RXR.administrationMethod.altText = rxrSeg['RXR.4']['RXR.4.5']; }
				if (rxrSeg['RXR.4']['RXR.4.6'].toString() != "") { orc[index].RXR.administrationMethod.altCodingSystem = rxrSeg['RXR.4']['RXR.4.6']; }
				if (Object.keys(orc[index].RXR.administrationMethod).length == 0) {
					delete orc[index].RXR.administrationMethod;
				}
			}
		}

		// clean up RXR
		if (Object.keys(orc[index].RXR).length == 0) {
			delete orc[index].RXR;
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the RXO and other segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// OBX (ORC)
		var obxGroup = getSegmentsAfter(msg,seg,'OBX',true);
		if (msg['OBX'].length() > 0 && orc) {
			orc[index].OBX = [];
			var indexOBX = 0;
			for each (obxSeg in obxGroup) {
				orc[index].OBX[indexOBX] = {};

				// OBX.1, setId
				if (obxSeg['OBX.1']['OBX.1.1'].toString() != "") { orc[index].OBX[indexOBX].setId = obxSeg['OBX.1']['OBX.1.1']; }

				// OBX.2, valueType
				if (obxSeg['OBX.2']['OBX.2.1'].toString() != "") { orc[index].OBX[indexOBX].valueType = obxSeg['OBX.2']['OBX.2.1']; }

				// OBX.3, identifier
				orc[index].OBX[indexOBX].identifier = {};
				if (obxSeg['OBX.3']['OBX.3.1'].toString() != "") { orc[index].OBX[indexOBX].identifier.id = obxSeg['OBX.3']['OBX.3.1']; }
				if (obxSeg['OBX.3']['OBX.3.2'].toString() != "") { orc[index].OBX[indexOBX].identifier.text = obxSeg['OBX.3']['OBX.3.2']; }
				if (obxSeg['OBX.3']['OBX.3.3'].toString() != "") { orc[index].OBX[indexOBX].identifier.codingSystem = obxSeg['OBX.3']['OBX.3.3']; }
				if (obxSeg['OBX.3']['OBX.3.4'].toString() != "") { orc[index].OBX[indexOBX].identifier.altId = obxSeg['OBX.3']['OBX.3.4']; }
				if (obxSeg['OBX.3']['OBX.3.5'].toString() != "") { orc[index].OBX[indexOBX].identifier.altText = obxSeg['OBX.3']['OBX.3.5']; }
				if (obxSeg['OBX.3']['OBX.3.6'].toString() != "") { orc[index].OBX[indexOBX].identifier.altCodingSystem = obxSeg['OBX.3']['OBX.3.6']; }
				if (Object.keys(orc[index].OBX[indexOBX].identifier).length == 0) {
					delete orc[index].OBX[indexOBX].identifier;
				}

				// OBX.4, subId
				if (obxSeg['OBX.4']['OBX.4.1'].toString() != "") { orc[index].OBX[indexOBX].subId = obxSeg['OBX.4']['OBX.4.1']; }

				// OBX.5, observationValue
				orc[index].OBX[indexOBX].observationValue = [];
				for each (field in obxSeg['OBX.5']) {
					if (field['OBX.5.1'].toString() != "") { 
						orc[index].OBX[indexOBX].observationValue.push(field['OBX.5.1']); 
					} else if (field['OBX.5.5'].toString() != "") { 
						orc[index].OBX[indexOBX].observationValue.push(field['OBX.5.5']); 
					}
				}
				if (orc[index].OBX[indexOBX].observationValue.length == 0) {
					delete orc[index].OBX[indexOBX].observationValue;
				}

				// OBX.6, units
				orc[index].OBX[indexOBX].units = {};
				if (obxSeg['OBX.6']['OBX.6.1'].toString() != "") { orc[index].OBX[indexOBX].units.id = obxSeg['OBX.6']['OBX.6.1']; }
				if (obxSeg['OBX.6']['OBX.6.2'].toString() != "") { orc[index].OBX[indexOBX].units.text = obxSeg['OBX.6']['OBX.6.2']; }
				if (obxSeg['OBX.6']['OBX.6.3'].toString() != "") { orc[index].OBX[indexOBX].units.codingSystem = obxSeg['OBX.6']['OBX.6.3']; }
				if (obxSeg['OBX.6']['OBX.6.4'].toString() != "") { orc[index].OBX[indexOBX].units.altId = obxSeg['OBX.6']['OBX.6.4']; }
				if (obxSeg['OBX.6']['OBX.6.5'].toString() != "") { orc[index].OBX[indexOBX].units.altText = obxSeg['OBX.6']['OBX.6.5']; }
				if (obxSeg['OBX.6']['OBX.6.6'].toString() != "") { orc[index].OBX[indexOBX].units.altCodingSystem = obxSeg['OBX.6']['OBX.6.6']; }
				if (Object.keys(orc[index].OBX[indexOBX].units).length == 0) {
					delete orc[index].OBX[indexOBX].units;
				}

				// OBX.7, referenceRange
				if (obxSeg['OBX.7']['OBX.7.1'].toString() != "") { orc[index].OBX[indexOBX].referenceRange = obxSeg['OBX.7']['OBX.7.1']; }

				// OBX.8, abnormalFlag
				orc[index].OBX[indexOBX].abnormalFlag = [];
				for each (field in obxSeg['OBX.9']) {
					if (field['OBX.9.1'].toString() != "") { 
						orc[index].OBX[indexOBX].abnormalFlag.push(field['OBX.9.1']); 
					}
				}
				if (orc[index].OBX[indexOBX].abnormalFlag.length == 0) {
					delete orc[index].OBX[indexOBX].abnormalFlag;
				}

				// OBX.9, probability
				if (obxSeg['OBX.9']['OBX.9.1'].toString() != "") { orc[index].OBX[indexOBX].probability = obxSeg['OBX.9']['OBX.9.1']; }

				// OBX.10, natureOfAbnormal
				orc[index].OBX[indexOBX].natureOfAbnormal = [];
				for each (field in obxSeg['OBX.10']) {
					if (field['OBX.10.1'].toString() != "") { 
						orc[index].OBX[indexOBX].natureOfAbnormal.push(field['OBX.10.1']); 
					}
				}
				if (orc[index].OBX[indexOBX].natureOfAbnormal.length == 0) {
					delete orc[index].OBX[indexOBX].natureOfAbnormal;
				}

				// OBX.11, observeResultStatus
				if (obxSeg['OBX.11']['OBX.11.1'].toString() != "") { orc[index].OBX[indexOBX].observeResultStatus = obxSeg['OBX.11']['OBX.11.1']; }

				// OBX.12, effectiveDateLastNormalValue
				if (obxSeg['OBX.12']['OBX.12.1'].toString() != "") { orc[index].OBX[indexOBX].effectiveDateLastNormalValue = obxSeg['OBX.12']['OBX.12.1']; }

				// OBX.13, definedAccessChecks
				if (obxSeg['OBX.13']['OBX.13.1'].toString() != "") { orc[index].OBX[indexOBX].definedAccessChecks = obxSeg['OBX.13']['OBX.13.1']; }

				// OBX.14, observationDateTime
				if (obxSeg['OBX.14']['OBX.14.1'].toString() != "") { orc[index].OBX[indexOBX].observationDateTime = obxSeg['OBX.14']['OBX.14.1']; }

				// OBX.15, producersId
				orc[index].OBX[indexOBX].producersId = {};
				if (obxSeg['OBX.15']['OBX.15.1'].toString() != "") { orc[index].OBX[indexOBX].producersId.id = obxSeg['OBX.15']['OBX.15.1']; }
				if (obxSeg['OBX.15']['OBX.15.2'].toString() != "") { orc[index].OBX[indexOBX].producersId.text = obxSeg['OBX.15']['OBX.15.2']; }
				if (obxSeg['OBX.15']['OBX.15.3'].toString() != "") { orc[index].OBX[indexOBX].producersId.codingSystem = obxSeg['OBX.15']['OBX.15.3']; }
				if (obxSeg['OBX.15']['OBX.15.4'].toString() != "") { orc[index].OBX[indexOBX].producersId.altId = obxSeg['OBX.15']['OBX.15.4']; }
				if (obxSeg['OBX.15']['OBX.15.5'].toString() != "") { orc[index].OBX[indexOBX].producersId.altText = obxSeg['OBX.15']['OBX.15.5']; }
				if (obxSeg['OBX.15']['OBX.15.6'].toString() != "") { orc[index].OBX[indexOBX].producersId.altCodingSystem = obxSeg['OBX.15']['OBX.15.6']; }
				if (Object.keys(orc[index].OBX[indexOBX].producersId).length == 0) {
					delete orc[index].OBX[indexOBX].producersId;
				}

				// OBX.16, responsibleObserver
				orc[index].OBX[indexOBX].responsibleObserver = {};
				if (obxSeg['OBX.16']['OBX.16.1'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.id = obxSeg['OBX.16']['OBX.16.1']; }
				if (obxSeg['OBX.16']['OBX.16.2'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.lastName = obxSeg['OBX.16']['OBX.16.2']; }
				if (obxSeg['OBX.16']['OBX.16.3'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.firstName = obxSeg['OBX.16']['OBX.16.3']; }
				if (obxSeg['OBX.16']['OBX.16.4'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.middleInitOrName = obxSeg['OBX.16']['OBX.16.4']; }
				if (obxSeg['OBX.16']['OBX.16.5'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.suffix = obxSeg['OBX.16']['OBX.16.5']; }
				if (obxSeg['OBX.16']['OBX.16.6'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.prefix = obxSeg['OBX.16']['OBX.16.6']; }
				if (obxSeg['OBX.16']['OBX.16.7'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.degree = obxSeg['OBX.16']['OBX.16.7']; }
				if (obxSeg['OBX.16']['OBX.16.8'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.sourceTable = obxSeg['OBX.16']['OBX.16.8']; }
				orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority = {};
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1']; }
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.universalId = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2']; }
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.universalIdType = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3']; }
				if (Object.keys(orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority).length == 0) {
					if (obxSeg['OBX.16']['OBX.16.9'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']; }
					else delete orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority;
				}
				if (obxSeg['OBX.16']['OBX.16.10'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.nameTypeCode = obxSeg['OBX.16']['OBX.16.10']; }
				if (obxSeg['OBX.16']['OBX.16.11'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.identifierCheckDigit = obxSeg['OBX.16']['OBX.16.11']; }
				if (obxSeg['OBX.16']['OBX.16.12'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.codeIdentifyingCheckDigitScheme = obxSeg['OBX.16']['OBX.16.12']; }
				if (obxSeg['OBX.16']['OBX.16.13'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.identifierTypeCode = obxSeg['OBX.16']['OBX.16.13']; }
				orc[index].OBX[indexOBX].responsibleObserver.assigningFacility = {};
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1']; }
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalId = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2']; }
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalIdType = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3']; }
				if (Object.keys(orc[index].OBX[indexOBX].responsibleObserver.assigningFacility).length == 0) {
					if (obxSeg['OBX.16']['OBX.16.14'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']; }
					else delete orc[index].OBX[indexOBX].responsibleObserver.assigningFacility;
				}
				if (Object.keys(orc[index].OBX[indexOBX].responsibleObserver).length == 0) {
					delete orc[index].OBX[indexOBX].responsibleObserver;
				}

				// OBX.17, observationMethod
				orc[index].OBX[indexOBX].observationMethod = [];
				for each (field in obxSeg['OBX.17']) {
					var block = {};
					if (field['OBX.17.1'].toString() != "") { block.id = field['OBX.17.1']; }
					if (field['OBX.17.2'].toString() != "") { block.text = field['OBX.17.2']; }
					if (field['OBX.17.3'].toString() != "") { block.codingSystem = field['OBX.17.3']; }
					if (field['OBX.17.4'].toString() != "") { block.altId = field['OBX.17.4']; }
					if (field['OBX.17.5'].toString() != "") { block.altText = field['OBX.17.5']; }
					if (field['OBX.17.6'].toString() != "") { block.altCodingSystem = field['OBX.17.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].OBX[indexOBX].observationMethod.push(block);
					}
				}
				if (orc[index].OBX[indexOBX].observationMethod.length == 0) {
					delete orc[index].OBX[indexOBX].observationMethod;
				}

				// NTE (OBX)
				var nteGroup = getSegmentsAfter(msg,obxSeg,'NTE',true);
				if (nteGroup.length > 0) {
					orc[index].OBX[indexOBX].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						orc[index].OBX[indexOBX].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { orc[index].OBX[indexOBX].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { orc[index].OBX[indexOBX].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						orc[index].OBX[indexOBX].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								orc[index].OBX[indexOBX].NTE[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (orc[index].OBX[indexOBX].NTE[indexNTE].comment.length == 0) {
							delete orc[index].OBX[indexOBX].NTE[indexNTE].comment;
						}

						// clean up NTE and increment indexOBX
						if (orc[index].OBX[indexOBX].NTE.length == 0) {
							delete norc[index].OBX[indexOBX].NTE;
						} else {
							indexNTE++;
						}
					}
				}

				// clean up OBX and increment indexOBX
				if (Object.keys(orc[index].OBX[indexOBX]).length == 0) {
					delete orc[index].OBX[indexOBX];
				} else {
					indexOBX++;
				}
			}

			// Clean up the OBX object if it's empty
			if (orc[index].OBX[0] == null){
				delete orc[index].OBX;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

