			for each (obxSeg in obxGroup) {
				orc[index].OBX[indexOBX] = {};

				// OBX.1, setId
				if (obxSeg['OBX.1']['OBX.1.1'].toString() != "") { orc[index].OBX[indexOBX].setId = obxSeg['OBX.1']['OBX.1.1']; }

				// OBX.2, valueType
