				// OBX.3, identifier
				orc[index].OBX[indexOBX].identifier = {};
				if (obxSeg['OBX.3']['OBX.3.1'].toString() != "") { orc[index].OBX[indexOBX].identifier.id = obxSeg['OBX.3']['OBX.3.1']; }
				if (obxSeg['OBX.3']['OBX.3.2'].toString() != "") { orc[index].OBX[indexOBX].identifier.text = obxSeg['OBX.3']['OBX.3.2']; }
				if (obxSeg['OBX.3']['OBX.3.3'].toString() != "") { orc[index].OBX[indexOBX].identifier.codingSystem = obxSeg['OBX.3']['OBX.3.3']; }
