				if (Object.keys(orc[index].OBX[indexOBX].identifier).length == 0) {
					delete orc[index].OBX[indexOBX].identifier;
				}

				// OBX.4, subId
				if (obxSeg['OBX.4']['OBX.4.1'].toString() != "") { orc[index].OBX[indexOBX].subId = obxSeg['OBX.4']['OBX.4.1']; }

