					if (field['OBX.5.1'].toString() != "") { 
						orc[index].OBX[indexOBX].observationValue.push(field['OBX.5.1']); 
					} else if (field['OBX.5.5'].toString() != "") { 
						orc[index].OBX[indexOBX].observationValue.push(field['OBX.5.5']); 
					}
				}
				if (orc[index].OBX[indexOBX].observationValue.length == 0) {
					delete orc[index].OBX[indexOBX].observationValue;
				}

				// OBX.6, units
				orc[index].OBX[indexOBX].units = {};
				if (obxSeg['OBX.6']['OBX.6.1'].toString() != "") { orc[index].OBX[indexOBX].units.id = obxSeg['OBX.6']['OBX.6.1']; }
				if (obxSeg['OBX.6']['OBX.6.2'].toString() != "") { orc[index].OBX[indexOBX].units.text = obxSeg['OBX.6']['OBX.6.2']; }
				if (obxSeg['OBX.6']['OBX.6.3'].toString() != "") { orc[index].OBX[indexOBX].units.codingSystem = obxSeg['OBX.6']['OBX.6.3']; }
				if (obxSeg['OBX.6']['OBX.6.4'].toString() != "") { orc[index].OBX[indexOBX].units.altId = obxSeg['OBX.6']['OBX.6.4']; }
				if (obxSeg['OBX.6']['OBX.6.5'].toString() != "") { orc[index].OBX[indexOBX].units.altText = obxSeg['OBX.6']['OBX.6.5']; }
				if (obxSeg['OBX.6']['OBX.6.6'].toString() != "") { orc[index].OBX[indexOBX].units.altCodingSystem = obxSeg['OBX.6']['OBX.6.6']; }
				if (Object.keys(orc[index].OBX[indexOBX].units).length == 0) {
					delete orc[index].OBX[indexOBX].units;
				}

				// OBX.7, referenceRange
				if (obxSeg['OBX.7']['OBX.7.1'].toString() != "") { orc[index].OBX[indexOBX].referenceRange = obxSeg['OBX.7']['OBX.7.1']; }

				// OBX.8, abnormalFlag
				orc[index].OBX[indexOBX].abnormalFlag = [];
				for each (field in obxSeg['OBX.9']) {
					if (field['OBX.9.1'].toString() != "") { 
						orc[index].OBX[indexOBX].abnormalFlag.push(field['OBX.9.1']); 
					}
				}
				if (orc[index].OBX[indexOBX].abnormalFlag.length == 0) {
					delete orc[index].OBX[indexOBX].abnormalFlag;
				}

				// OBX.9, probability
				if (obxSeg['OBX.9']['OBX.9.1'].toString() != "") { orc[index].OBX[indexOBX].probability = obxSeg['OBX.9']['OBX.9.1']; }

				// OBX.10, natureOfAbnormal
				orc[index].OBX[indexOBX].natureOfAbnormal = [];
				for each (field in obxSeg['OBX.10']) {
					if (field['OBX.10.1'].toString() != "") { 
						orc[index].OBX[indexOBX].natureOfAbnormal.push(field['OBX.10.1']); 
					}
				}
				if (orc[index].OBX[indexOBX].natureOfAbnormal.length == 0) {
					delete orc[index].OBX[indexOBX].natureOfAbnormal;
				}

				// OBX.11, observeResultStatus
				if (obxSeg['OBX.11']['OBX.11.1'].toString() != "") { orc[index].OBX[indexOBX].observeResultStatus = obxSeg['OBX.11']['OBX.11.1']; }

				// OBX.12, effectiveDateLastNormalValue
				if (obxSeg['OBX.12']['OBX.12.1'].toString() != "") { orc[index].OBX[indexOBX].effectiveDateLastNormalValue = obxSeg['OBX.12']['OBX.12.1']; }

				// OBX.13, definedAccessChecks
				if (obxSeg['OBX.13']['OBX.13.1'].toString() != "") { orc[index].OBX[indexOBX].definedAccessChecks = obxSeg['OBX.13']['OBX.13.1']; }

				// OBX.14, observationDateTime
				if (obxSeg['OBX.14']['OBX.14.1'].toString() != "") { orc[index].OBX[indexOBX].observationDateTime = obxSeg['OBX.14']['OBX.14.1']; }

				// OBX.15, producersId
				orc[index].OBX[indexOBX].producersId = {};
				if (obxSeg['OBX.15']['OBX.15.1'].toString() != "") { orc[index].OBX[indexOBX].producersId.id = obxSeg['OBX.15']['OBX.15.1']; }
				if (obxSeg['OBX.15']['OBX.15.2'].toString() != "") { orc[index].OBX[indexOBX].producersId.text = obxSeg['OBX.15']['OBX.15.2']; }
				if (obxSeg['OBX.15']['OBX.15.3'].toString() != "") { orc[index].OBX[indexOBX].producersId.codingSystem = obxSeg['OBX.15']['OBX.15.3']; }
				if (obxSeg['OBX.15']['OBX.15.4'].toString() != "") { orc[index].OBX[indexOBX].producersId.altId = obxSeg['OBX.15']['OBX.15.4']; }
				if (obxSeg['OBX.15']['OBX.15.5'].toString() != "") { orc[index].OBX[indexOBX].producersId.altText = obxSeg['OBX.15']['OBX.15.5']; }
				if (obxSeg['OBX.15']['OBX.15.6'].toString() != "") { orc[index].OBX[indexOBX].producersId.altCodingSystem = obxSeg['OBX.15']['OBX.15.6']; }
				if (Object.keys(orc[index].OBX[indexOBX].producersId).length == 0) {
					delete orc[index].OBX[indexOBX].producersId;
				}

				// OBX.16, responsibleObserver
				orc[index].OBX[indexOBX].responsibleObserver = {};
				if (obxSeg['OBX.16']['OBX.16.1'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.id = obxSeg['OBX.16']['OBX.16.1']; }
				if (obxSeg['OBX.16']['OBX.16.2'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.lastName = obxSeg['OBX.16']['OBX.16.2']; }
				if (obxSeg['OBX.16']['OBX.16.3'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.firstName = obxSeg['OBX.16']['OBX.16.3']; }
				if (obxSeg['OBX.16']['OBX.16.4'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.middleInitOrName = obxSeg['OBX.16']['OBX.16.4']; }
				if (obxSeg['OBX.16']['OBX.16.5'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.suffix = obxSeg['OBX.16']['OBX.16.5']; }
				if (obxSeg['OBX.16']['OBX.16.6'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.prefix = obxSeg['OBX.16']['OBX.16.6']; }
				if (obxSeg['OBX.16']['OBX.16.7'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.degree = obxSeg['OBX.16']['OBX.16.7']; }
				if (obxSeg['OBX.16']['OBX.16.8'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.sourceTable = obxSeg['OBX.16']['OBX.16.8']; }
				orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority = {};
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.1']; }
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.universalId = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.2']; }
				if (obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.universalIdType = obxSeg['OBX.16']['OBX.16.9']['OBX.16.9.3']; }
				if (Object.keys(orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority).length == 0) {
					if (obxSeg['OBX.16']['OBX.16.9'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority.id = obxSeg['OBX.16']['OBX.16.9']; }
					else delete orc[index].OBX[indexOBX].responsibleObserver.assigningAuthority;
				}
				if (obxSeg['OBX.16']['OBX.16.10'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.nameTypeCode = obxSeg['OBX.16']['OBX.16.10']; }
				if (obxSeg['OBX.16']['OBX.16.11'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.identifierCheckDigit = obxSeg['OBX.16']['OBX.16.11']; }
				if (obxSeg['OBX.16']['OBX.16.12'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.codeIdentifyingCheckDigitScheme = obxSeg['OBX.16']['OBX.16.12']; }
				if (obxSeg['OBX.16']['OBX.16.13'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.identifierTypeCode = obxSeg['OBX.16']['OBX.16.13']; }
				orc[index].OBX[indexOBX].responsibleObserver.assigningFacility = {};
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.1']; }
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalId = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.2']; }
				if (obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.universalIdType = obxSeg['OBX.16']['OBX.16.14']['OBX.16.14.3']; }
				if (Object.keys(orc[index].OBX[indexOBX].responsibleObserver.assigningFacility).length == 0) {
					if (obxSeg['OBX.16']['OBX.16.14'].toString() != "") { orc[index].OBX[indexOBX].responsibleObserver.assigningFacility.id = obxSeg['OBX.16']['OBX.16.14']; }
					else delete orc[index].OBX[indexOBX].responsibleObserver.assigningFacility;
				}
				if (Object.keys(orc[index].OBX[indexOBX].responsibleObserver).length == 0) {
					delete orc[index].OBX[indexOBX].responsibleObserver;
				}

				// OBX.17, observationMethod
				orc[index].OBX[indexOBX].observationMethod = [];
				for each (field in obxSeg['OBX.17']) {
					var block = {};
					if (field['OBX.17.1'].toString() != "") { block.id = field['OBX.17.1']; }
					if (field['OBX.17.2'].toString() != "") { block.text = field['OBX.17.2']; }
					if (field['OBX.17.3'].toString() != "") { block.codingSystem = field['OBX.17.3']; }
					if (field['OBX.17.4'].toString() != "") { block.altId = field['OBX.17.4']; }
					if (field['OBX.17.5'].toString() != "") { block.altText = field['OBX.17.5']; }
					if (field['OBX.17.6'].toString() != "") { block.altCodingSystem = field['OBX.17.6']; }
					if (Object.keys(block).length > 0) {
						orc[index].OBX[indexOBX].observationMethod.push(block);
					}
				}
				if (orc[index].OBX[indexOBX].observationMethod.length == 0) {
					delete orc[index].OBX[indexOBX].observationMethod;
				}

				// NTE (OBX)
				var nteGroup = getSegmentsAfter(msg,obxSeg,'NTE',true);
				if (nteGroup.length > 0) {
					orc[index].OBX[indexOBX].NTE = [];
					var indexNTE = 0;
					for each (nteSeg in nteGroup) {
						orc[index].OBX[indexOBX].NTE[indexNTE] = {};

						// NTE.1, setId
						if (nteSeg['NTE.1']['NTE.1.1'].toString() != "") { orc[index].OBX[indexOBX].NTE[indexNTE].setId = nteSeg['NTE.1']['NTE.1.1']; }

						// NTE.2, sourceOfComment
						if (nteSeg['NTE.2']['NTE.2.1'].toString() != "") { orc[index].OBX[indexOBX].NTE[indexNTE].sourceOfComment = nteSeg['NTE.2']['NTE.2.1']; }

						// NTE.3, comment
						orc[index].OBX[indexOBX].NTE[indexNTE].comment = [];
						for each (field in nteSeg['NTE.3']) {
							var block = {};
							if (field['NTE.3.1'].toString() != "") { 
								orc[index].OBX[indexOBX].NTE[indexNTE].comment.push(field['NTE.3.1']);
							}
						}
						if (orc[index].OBX[indexOBX].NTE[indexNTE].comment.length == 0) {
							delete orc[index].OBX[indexOBX].NTE[indexNTE].comment;
						}

						// clean up NTE and increment indexOBX
						if (orc[index].OBX[indexOBX].NTE.length == 0) {
							delete norc[index].OBX[indexOBX].NTE;
						} else {
							indexNTE++;
						}
					}
				}

				// clean up OBX and increment indexOBX
				if (Object.keys(orc[index].OBX[indexOBX]).length == 0) {
					delete orc[index].OBX[indexOBX];
				} else {
					indexOBX++;
				}
			}

			// Clean up the OBX object if it's empty
			if (orc[index].OBX[0] == null){
				delete orc[index].OBX;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
//channelMap.put("orc", orc); // uncomment to save

// Re-add the ORC segment to the hl7JSON object
if (orc && Object.keys(orc).length != 0) {
	var hl7JSON = JSON.parse($('hl7JSON'));
	hl7JSON.content.ORC = orc;
	channelMap.put("hl7JSON", JSON.stringify(hl7JSON));
}

step.script:
// Pull out the ORC JSON to embed the RXO and other segments in
var orc = JSON.parse($('hl7JSON')).content.ORC;

// ORC iteration, in order to grab related RXO's
if (msg['ORC'].length() > 0) {

	// ORC is a required to have a RXO, but if the ORC was blank and was removed from the JSON, just create a new one to add the RXO into
	if (!orc) {
		orc = [{}];
	}

	var index = 0;
	for each (seg in msg.ORC) {

		// CTI
		var ctiGroup = getSegmentsAfter(msg,seg,'CTI',true);
		if (msg['CTI'].length() > 0) {
			orc[index].CTI = [];
			var indexCTI = 0;
			for each (ctiSeg in ctiGroup) {
				orc[index].CTI[indexCTI] = {};

				// CTI.1, sponsorStudyIdentifier
				orc[index].CTI[indexCTI].sponsorStudyIdentifier = {};
				if (ctiSeg['CTI.1']['CTI.1.1'].toString() != "") { orc[index].CTI[indexCTI].sponsorStudyIdentifier.id = ctiSeg['CTI.1']['CTI.1.1']; }
				if (ctiSeg['CTI.1']['CTI.1.2'].toString() != "") { orc[index].CTI[indexCTI].sponsorStudyIdentifier.namespaceId = ctiSeg['CTI.1']['CTI.1.2']; }
				if (ctiSeg['CTI.1']['CTI.1.3'].toString() != "") { orc[index].CTI[indexCTI].sponsorStudyIdentifier.universalId = ctiSeg['CTI.1']['CTI.1.3']; }
				if (ctiSeg['CTI.1']['CTI.1.4'].toString() != "") { orc[index].CTI[indexCTI].sponsorStudyIdentifier.unniveralIdType = ctiSeg['CTI.1']['CTI.1.4']; }
				if (Object.keys(orc[index].CTI[indexCTI].sponsorStudyIdentifier).length == 0) {
					delete orc[index].CTI[indexCTI].sponsorStudyIdentifier;
				}

				// CTI.2, studyPhaseIdentifier
				orc[index].CTI[indexCTI].studyPhaseIdentifier = {};
				if (ctiSeg['CTI.2']['CTI.2.1'].toString() != "") { orc[index].CTI[indexCTI].studyPhaseIdentifier.id = ctiSeg['CTI.2']['CTI.2.1']; }
				if (ctiSeg['CTI.2']['CTI.2.2'].toString() != "") { orc[index].CTI[indexCTI].studyPhaseIdentifier.text = ctiSeg['CTI.2']['CTI.2.2']; }
				if (ctiSeg['CTI.2']['CTI.2.3'].toString() != "") { orc[index].CTI[indexCTI].studyPhaseIdentifier.codingSystem = ctiSeg['CTI.2']['CTI.2.3']; }
				if (ctiSeg['CTI.2']['CTI.2.4'].toString() != "") { orc[index].CTI[indexCTI].studyPhaseIdentifier.altId = ctiSeg['CTI.2']['CTI.2.4']; }
				if (ctiSeg['CTI.2']['CTI.2.5'].toString() != "") { orc[index].CTI[indexCTI].studyPhaseIdentifier.altText = ctiSeg['CTI.2']['CTI.2.5']; }
				if (ctiSeg['CTI.2']['CTI.2.6'].toString() != "") { orc[index].CTI[indexCTI].studyPhaseIdentifier.altCodingSystem = ctiSeg['CTI.2']['CTI.2.6']; }
				if (Object.keys(orc[index].CTI[indexCTI].studyPhaseIdentifier).length == 0) {
					delete orc[index].CTI[indexCTI].studyPhaseIdentifier;
				}

				// CTI.3, studyScheduledTimePoint
				orc[index].CTI[indexCTI].studyScheduledTimePoint = {};
				if (ctiSeg['CTI.3']['CTI.3.1'].toString() != "") { orc[index].CTI[indexCTI].studyScheduledTimePoint.id = ctiSeg['CTI.3']['CTI.3.1']; }
				if (ctiSeg['CTI.3']['CTI.3.2'].toString() != "") { orc[index].CTI[indexCTI].studyScheduledTimePoint.text = ctiSeg['CTI.3']['CTI.3.2']; }
				if (ctiSeg['CTI.3']['CTI.3.3'].toString() != "") { orc[index].CTI[indexCTI].studyScheduledTimePoint.codingSystem = ctiSeg['CTI.3']['CTI.3.3']; }
				if (ctiSeg['CTI.3']['CTI.3.4'].toString() != "") { orc[index].CTI[indexCTI].studyScheduledTimePoint.altId = ctiSeg['CTI.3']['CTI.3.4']; }
				if (ctiSeg['CTI.3']['CTI.3.5'].toString() != "") { orc[index].CTI[indexCTI].studyScheduledTimePoint.altText = ctiSeg['CTI.3']['CTI.3.5']; }
				if (ctiSeg['CTI.3']['CTI.3.6'].toString() != "") { orc[index].CTI[indexCTI].studyScheduledTimePoint.altCodingSystem = ctiSeg['CTI.3']['CTI.3.6']; }
				if (Object.keys(orc[index].CTI[indexCTI].studyScheduledTimePoint).length == 0) {
					delete orc[index].CTI[indexCTI].studyScheduledTimePoint;
				}

				// clean up CTI and increment indexCTI
				if (Object.keys(orc[index].CTI[indexCTI]).length == 0) {
					delete orc[index].CTI[indexCTI];
				} else {
					indexCTI++;
				}
			}

			// Clean up the CTI object if it's empty
			if (orc[index].CTI[0] == null){
				delete orc[index].CTI;
			}
		}

		// Increment the ORC index
		index++;
	}
}

// Optional: save a copy of the completed ORC segment for reference later
