step.script:
var params = new java.lang.String(connectorMessage.getRawData().toString());
	try{
	var messageType = msg['row']['column1'].toString();
	var eventType = msg['row']['column2'].toString();



	channelMap.put("messageType",messageType);
	channelMap.put("eventType",eventType);
	var sample = generateMessage(messageType,eventType);
	channelMap.put("sample",sample);
	responseMap.put("sampleResp",sample);
	channelMap.put("respCode","200");
	channelMap.put('mirth_type',messageType+"_"+eventType);}
	catch(e){
		responseMap.put("sampleResp",e)
		channelMap.put("respCode","500");
		throw(e);	
		}
//================================================================================
//Message Functions
//================================================================================
function generateMessage(messageType,eventType){
	var msg = "";
	switch(messageType){
	case "ADT": msg = ADT(eventType);
	break;
	case "SIU": msg = SIU(eventType);
	break;
	case "SRM": msg = SRM(eventType);
	break;
	case "MDM": msg = MDM(eventType);
	break;
	case "ORU": msg = ORU(eventType);
	break;
	case "ORM": msg = ORM(eventType);
	break;
	case "RAS": msg = "";//RAS(eventType);
	default: throw("Not a supported message type. Currently supports ADT, SIU, SRM, MDM, ORU, ORM. Try sending ADT^A01 to receive a sample message");
	}
	return msg;
};
function ORU(eventType){
	if (eventType !== "R01"){throw ("Not a supported event type for ORU. Supported event types are: R01. Try sending ORU^R01 to receive a sample message")}
	var pat = person();
	var ord = order();
	var obs = pathObs(ord);
	msg = MSH("ORU",eventType)+PID(pat)+PD1(pat)+NTE(1)+
	PV1(pat)+PV2(pat)+
	ORC(ord)+OBR(ord)+NTE(2)+OBX(obs)+NTE(3);
	return msg.toUpperCase();
}
function SIU(eventType){
	var pat = person();
	var appt = appointment();
	var msg = ""; //add aig
	var ord = order();
	var obs = pathObs(ord);
	msg = MSH("SIU",eventType)+SCH(eventType,appt)+NTE(1)+ 
	PID(pat)+PV1(pat)+PV2(pat)+OBX(obs)+DG1()+
	RGS(appt)+AIS(appt)+NTE(2)+AIG(appt)+NTE(3)+AIL(appt)+NTE(4)+AIP(appt)+NTE(5);
	return msg.toUpperCase();
}
function SRM(eventType){
	var pat = person();
	var appt = appointment();
	var msg = ""; //add aig
	var ord = order();
	var obs = pathObs(ord);
	msg = MSH("SRM",eventType)+ARQ(eventType,appt)+APR()+NTE(1)+ 
	PID(pat)+PV1(pat)+PV2(pat)+OBX(obs)+DG1()+
	RGS(appt)+AIS(appt)+APR()+NTE(2)+
	AIG(appt)+APR()+NTE(3)+
	AIL(appt)+APR()+NTE(4)+
	AIP(appt)+APR()+NTE(5);
	return msg.toUpperCase();
}
function MDM(eventType){
	var pat = person();
	var appt = appointment();
	var msg = "";
	msg = MSH("MDM",eventType) + EVN("MDM",eventType) + PID(pat) + PV1(pat) + TXA() + mdmOBX(eventType);
	return msg.toUpperCase();
}
//in progress *******************
function ORM(eventType){
	if (eventType !== "O01"){throw("Not a supported event type for ORM. Supported event types are: O01. Try sending ORM^O01 to receive a sample message");}
	var pat = person();
	var rel = relative(pat);
	var ord = order();
	var obs = pathObs(ord);
	var ins = insurance(pat);
	msg = MSH("ORM",eventType)+NTE(1)+
	PID(pat)+PD1(pat)+NTE(2)+
	PV1(pat)+PV2(pat)+
	IN1(ins)+IN2(ins)+IN3(ins)+GT1(rel)+AL1()+
	ORC(ord)+NTE(3)+DG1()+OBR(ord)+OBX(obs)+NTE(3);
	return msg.toUpperCase();
}
function ADT(eventType){
	switch(eventType){
	case "A01":
	case "A02":
	case "A03":
	case "A04":
	case "A05":
	case "A06":
	case "A07":
	case "A08":
	case "A13":
	case "A14":
	case "A28":
		break;
	default: throw("Not a supported message type for ADT. Supported event types are: A01, A02, A03, A04, A05, A06, A07, A08, A13, A14, A28. Try sending ADT^A01 to receive a sample message");
	break;
	}
	/*
	 * A01 - admit
	 * A02 - transfer
	 * A03 - discharge
	 * A04 - register
	 * A05 - preadmit
	 */

	var pat = person();
	var rel = relative(pat);
	var appt = appointment();
	var obs = pathObs(null);
	var prov = provider();
	var ins = insurance(pat);
	var msg = "";
	msg = MSH("ADT",eventType) + EVN("ADT",eventType) + PID(pat) + PD1(pat) + NK1(rel) + PV1(pat) + 
	PV2(pat) + DB1(pat) + OBX(obs) +AL1() + DG1() + //no DRG right now
	PR1() + ROL(prov) + GT1(rel) + IN1(ins) + IN2(ins) + IN3(ins)+ ACC();
	return msg.toUpperCase();
}
//================================================================================
//Segment Functions
//================================================================================
function MSH(messageType,eventType){
	var msh = "MSH|^~\&|" +
			"DATICA|" + //sending application
			valFromList([hospital(),clinic()])+"|" +
			"EXTERNAL EMR|" + //receiving application
			"EXTERNAL CLINIC|" +
			today()+"|"+ //message date / time
			"|" + //security
			messageType+"^"+eventType+"|"+
			randomInt(7)+"|"+ //message control ID
			valFromList(["D","P","T"]) +"|"+ //processing ID
			"2.3|" + //hl7 version
			"\n";
	return msh;
}
function PID(patient){
	var pid = "PID|1|" +
	patient.id.external + "|" + // external ID
	patient.id.internal + "|" + // internal ID
	patient.id.alternate + "|" + // alternate ID
	patient.name.last+"^"+patient.name.first + "|" + //patient name
	patient.name.mothersMaiden + "|" + // mother's maiden name
	patient.dob + "|" + //DOB
	patient.sex + "|" + //sex
	patient.name.alias + "|" + //alias
	patient.race + "|" + //race
	patient.address.street+"^"+"MADISON"+"^"+patient.address.state+"^"+patient.address.zip + "|" + //address
	patient.address.zip + "|" + //county code
	patient.phone.home + "|" + //home phone
	patient.phone.work + "|" + //business phone
	patient.language + "|" + //primary language
	patient.married + "|" + //marital status
	patient.religion + "|" + //religion
	patient.account + "|" + //account number
	patient.ssn + "|" + //ssn number
	patient.driversLicense + "|" + //drivers license number
	"" + "|" + //mother's identifier
	patient.ethnicity + "|" + //ethnic group
	patient.birthPlace + "|" + //birth place
	patient.multipleBirth + "|" + //multiple birth indicator
	patient.birthOrder + "|" + //birth order
	patient.citizenship + "|" + //citizenship
	"" + "|" + //veteran status
	"US^UNITED STATES OF AMERICA" + "|" + //
	"" + "|" + //patient death date
	"N" + "|" + //patient death indicator
	"" + "|\n"; //
	return pid.toUpperCase();
};
function EVN(messageType,eventType){
	evn = "EVN|" +
			messageType+"^"+eventType +"|" +
			today() + "|" +
			today() + "|" +
			"02|" + //event reason = physician request
			randomName()+"|" +
					today()+"\n"; 
	return evn.toUpperCase();
}
function PD1(patient){
	var pd1 = "PD1|" +
			patient.dependent +"|" + //dependent status
			patient.livingArrangement +"|" + //living arrangement
			valFromList([hospital(),clinic()])+"|" + //primary care facility
			formatProvider(provider())+"|" + //pcp
			patient.student + "|" + //student status
				"|" + //handicap
			patient.livingWill+"|" +
			patient.organDonor+"|" +
			patient.separateBill + "|" +
					"|" + //duplicate patient
					patient.publicity + "|" + //publicity indicator
					patient.protection + "\n";  
	return pd1.toUpperCase();
}
function DB1(patient){
	var personCode = valFromList(["PT","GT","IN","AP"]);
	var personId = null;
	if (personCode == "PT"){
		personId = patient.id.external;
	}else {
		personId = randomInt(6);
	}
	var db1 = "DB1|" +
			"1|" + //set ID
			personCode+"|"+
			personId+"|" +
			YN()+"|" + //disability visit?
			formatDate(randomDate(2016,2016))+ "|" + //disability start date
			formatDate(randomDate(2017,2017))+"|" + //disability end date
			formatDate(randomDate(2018,2018)) + //return to work date
			"\n";//unable to work date
	return db1;
}
function NK1(rel){ //requires a relative
	var nk1 = "NK1|1|" +
			rel.name.last+"^"+rel.name.first + "|"+ //name
			rel.relationship+"|" + //relationship
			rel.address.street+"^"+"MADISON"+"^"+rel.address.state+"^"+rel.address.zip + "|" + //address
			rel.phone.home + "|" + //home phone
			rel.phone.work + "|" + //business phone
			"N|" + //contact role = next of kin
			formatDate(randomDate(2012,2016))+"|" + //start date
			"|" + //end date
			rel.jobTitle+"|" + //job title
			"|" + //job code / class
			"|" + //employee number
			"|" + //organization name
			rel.married+"|" + //marital status
			rel.sex+"|" + //sex code
			rel.dob+"|" + //dob
			"U|" + //living dependency
			rel.ambulatoryStatus+"|" + //ambulatory status
			rel.citizenship+"|" + //citizenship
			rel.language+"|" + //language
			rel.livingArrangement+"|" + //living arrangement
			rel.publicity+"|" + //publicity indicator
			rel.protection+"|" + //protection indicator
			rel.student+"|" + //student indicator
			rel.religion+"|" + //religion
			rel.name.mothersMaiden+"|" + //mother's maiden name
			"US|" + //nationality
			rel.ethnicity+"|" + //ethnic group
			"|" + //contact reason - blank if not org
			"|" + //contact person name blank if not org
			"|" + //contact person phone blank if not org
			"|" + //contact person address blank if not org
			rel.id.external+"|" + //associated party identifiers 
			rel.jobStatus+"|" + //job status
			rel.race+"|" + //race
			"|" + //handicap
			rel.ssn+"|" + //ssn
			"\n";
	return nk1;
}
function PV1(patient){
	var pv1 = "PV1|1|" +
			valFromList(["E","I","O","P","R","B"])+"|" + //patient class
			patientLocation("A")+"|" + //assigned location
			valFromList(["A","E","L","R"])+"|" + //admission type
			randomInt(6)+"|" + //preadmit number
			patientLocation("B")+"|" + //prior patient location
			formatProvider(provider())+"|" + //attending doctor
			formatProvider(provider())+"|" + //referring doctor
			formatProvider(provider())+"|" + //consulting doctor
			valFromList(["MED","SUR","URO","PUL","CAR"])+"|" + //hospital service
			patientLocation("A")+"|" + //temporary location
			"N|" + //preadmit test indicator
			"|" + //readmission indicator - either R or null
			randomInt(1)+"|" + //admit source - 1-9
			patient.ambulatoryStatus+"|" + //ambulatory status
			"|" + //vip indicator - leaving blank
			formatProvider(provider())+"|" + //admitting doctor
			"U|" + //patient type - can't find suggested values
			randomInt(6)+"|" + //visit number
			"A23|" + //financial class
			"R|" + //charge price indicator
			"N|" + //courtesy code
			"G|" + //credit rating
			"44|" + //contract code
			formatDate(randomDate(2015,2016))+"|" + //contract effective date
			price(3)+"|" + //contract amount
			randomInt(1)+"|" + //contract period
			"C|" + //interest code
			"B|" + //transfer to bad debt code
			"|" + //transfer to bad debt date
			"|" + //bad debt agency code
			"|" + //bad debt transfer amount
			"|" + //bad debt recovery amount
			"|" + //delete account indicator
			"|" + //delete account date
			"|" + //discharge disposition
			"|" + //discharged to location
			"|" + //diet type
			"|" + //servicing facility
			"|" + //bed status
			"|" + //account status
			"|" + //pending location
			"|" + //prior temporary location
			"\n";
	return pv1;
}
