function SCH(eventType,appt){//* need to square up statuses with event type
	if (eventType == null){eventType = "S12";}
	var eventReason = "";
	switch(eventType){
	case "S12": eventReason = "BOOKED";
				appt.fillerStatusCode = "BOOKED"
		break;
	case "S14": appt.fillerStatusCode = "MODIFY";
	eventReason = "MODIFY";
		break;
	case "S13": appt.fillerStatusCode = "RESCHED";
				eventReason = "RESCHED";
		break;
	case "S15": appt.fillerStatusCode = "CANCELLED";
				eventReason = "CANCELED";
		break;
	case "S26": appt.fillerStatusCode = "NOSHOW";
	eventReason = "NOSHOW";
		break;
		default: throw("Not a valid event type for SIU. Supported event types are S12, S13, S14, S15, and S26. Try sending SIU^S12 to receive a sample message.");
	}
	var sch = "SCH|"+
	appt.placerId+"|"+ //placer appointment ID
	appt.fillerId+"|"+ //filler appointment ID
	"|"+ //occurrence number
	appt.placerGroup+"|"+ //placer group number
	"|"+ //schedule ID
	eventReason+"|"+ //event reason
	appt.reason+"|"+ //appointment reason
	appt.type+"|"+ //appointment type
	appt.duration+"|"+ //appointment duration
	"min|"+ //appointment duration units
	"|"+ //appointment timing quantity
	appt.placer.id.external+"^"+appt.placer.name.last+"^"+appt.placer.name.first+"|"+ //placer contact person
	appt.placer.phone.home+"|"+ //placer contact phone number
	appt.placer.address.street+"^"+"MADISON"+"^"+appt.placer.address.state+"^"+appt.placer.address.zip + "|" + //placer contact address
	patientLocation("E")+"|"+ //placer contact location
	appt.filler.id.external+"^"+appt.filler.name.last+"^"+appt.filler.name.first+"|"+ //filler contact person
	appt.filler.phone.home+"|"+ //placer contact phone number
	appt.filler.address.street+"^"+"MADISON"+"^"+appt.filler.address.state+"^"+appt.filler.address.zip + "|" + //placer contact address
	patientLocation("F")+"|"+ //placer contact location
	appt.placer.id.external+"^"+appt.placer.name.last+"^"+appt.placer.name.first+"|"+ //entered by person
	appt.placer.phone.home+"|"+ //entered by phone number
	patientLocation("E")+"|"+ //entered by location
	randomInt(7)+"|"+ //parent placer appointment ID
	randomInt(7)+"|"+//parent filler appointment ID
	appt.fillerStatusCode+"|"+//filler status code
	"\n";

	return sch.toUpperCase();
}
function RGS(){ //add realistic rgs resources
	var rgs = "RGS|" +
			"1|" +
			"|" +
			randomInt(9)+"|\n";
			return rgs;
}
function AIS(appt){

	var ais = "AIS|1|" +
			"|"+//segment action code
			randomInt(6)+"|"+//universal service identifier
			formatDate(adjustDate(appt.date,0,3))+"|"+//start date time
			"|"+//start date time offset
			"|"+//start date time offset units
			appt.duration+"|"+//duration
			"min|"+//duration units
			allowSubstitution()+"|"+//allow substitution code
			appt.fillerStatusCode+"\n";//filler status code

	return ais;
}
function AIG(appt){
	var resource = valFromList([ //https://hcpcs.codes/l-codes/
		"A4215^SYRINGE, STERILE, ANY SIZE, EACH^HCPCS",
		"A4217^Sterile water/saline, 500 ml^HCPCS",
		"A4259^LANCETS, PER BOX OF 100^HCPCS",
		"C1726^Catheter, balloon dilatation, non-vascular^HCPCS",
		"C1729^CATHETER, DRAINAGE^HCPCS",
	]);
	var aig = "AIG|1|" +
			"|"+//segment action code - only needed when modifying
			resource+"|"+//resource id
			"|"+//resource type
			"|"+//resource group
			"|"+//resource quantity
			"|"+//resource quantity units
			formatDate(adjustDate(appt.date,1,3))+"|"+//start date / time
			"|"+//start date time offset
			"|"+//start date time offset units
			appt.duration+"|"+//duration
			"min|"+//duration units
			allowSubstitution()+"|"+//allow substitution code
			appt.fillerStatusCode+"|"+//filler status code
			"\n";
	return aig;
}
function AIL(appt){
	var ail = "AIL|1|"+
	"|"+//segment action code
	randomInt(5)+"|"+//location resource ID
	"|"+//location type
	"|"+//location group
	formatDate(adjustDate(appt.date,1,3))+"|"+//start date / time
	"|"+//start date offset
	"|"+//start date time offset units
	appt.duration+"|"+//duration
	"min|"+//duration units
	allowSubstitution() +"|" +//allow substitution code
	appt.fillerStatusCode+"\n";
	return ail;
}
function AIP(appt){
	var prov = provider();
	var aip = "AIP|1|" +
			"|" + //segment action code
			formatProvider(provider())+"|" + //personnel resource id
			"|" + //resource role
			"|" + //resource group
			formatDate(adjustDate(appt.date,1,3))+"|" + //start date time
			"|" + //start date time offset
			"|" + //start date time offset units
			"|" + //duration
			"|" + //duration units
			allowSubstitution() + "|"+
			appt.fillerStatusCode+"\n";
	return aip;
}
function APR(){
	timeSelection = [
		"MON",
		"TUE",
		"WED",
		"THU",
		"FRI",
		"SAT",
		"SUN",
	];
	var apr = "APR|" +
			valFromList(["OK","NO"])+"-"+valFromList(timeSelection)+"|" +
					"\n";
	return apr;
}
function ARQ(eventType,appt){
	if (eventType == null){eventType = "S01";}
	var parentFillerId = randomInt(7);
	switch(eventType){
	case "S01": appt.fillerId = "";
	parentFillerId = "";
	break;
	case "S02":
		break;
	case "S03":
		break;
	case "S04":
		break;
	default: throw("Not a valid event type for SRM. Supported event types are: S01, S02, S03, S04. Try sending SRM^S01 to receive a sample message");
	}
	var arq = "ARQ|"+
	appt.placerId+"|"+//placer appointment ID
	appt.fillerId+"|"+//filler apopintment ID
	"1|"+//occurrence number
	appt.placerGroup+"|"+//placer group
	"|"+//schedule ID
	"|"+//request event reason
	appt.reason+"|"+//appointment reason
	appt.type+"|"+//appointment type
	appt.duration+"|"+//appointment duration
	"min|"+//appointment duration units
	"|"+//requested start date / time change
	"|"+//priority
	"|"+//repeating interval
	"|"+//repeating interval duration
	appt.placer.id.external+"^"+appt.placer.name.last+"^"+appt.placer.name.first+"|"+ //placer contact person
	appt.placer.phone.home+"|"+ //placer contact phone number
	appt.placer.address.street+"^"+"MADISON"+"^"+appt.placer.address.state+"^"+appt.placer.address.zip + "|" + //placer contact address
	patientLocation("E")+"|"+ //placer contact location
	appt.placer.id.external+"^"+appt.placer.name.last+"^"+appt.placer.name.first+"|"+ //entered by person
	appt.placer.phone.home+"|"+ //entered by phone number
	patientLocation("E")+"|"+ //entered by location
	randomInt(6)+"|"+//parent placer id
	parentFillerId;//parent filler id
	return arq.toUpperCase();
}
function ORC(){ //for oru
	var orc = "ORC|RE|"+
	randomInt(9)+"|"+//placer order number
	randomInt(9)+"|"+//filler order number
	"G|"+//placer group number
	"CM|"+//order status = completed
	"|"+//response flag
	"|"+//quantity timing
	"|"+//parent
	"|"+//date time of transaction
	"|"+//entered by
	"|"+//verified by
	"|"+//ordering provider
	"|"+//enterer's location
	"|"+//call back phone number
	"|"+//order effective date time
	"|"+//order control code reason
	"|"+//entering organization
	"|"+//entering device
	"|"+//action by
	"\n";
	return orc;
}
function OBR(ord){
	var obr = "OBR|1|"+
	ord.placerId+"|"+//placer order number
	ord.fillerId+"|"+//filler order number
	ord.universalServiceId+"|"+//universal service id
	"|"+//priority - NOT USED
	"|"+//requested date / time - NOT USED
	formatDate(ord.observationBegin)+"|"+//observation date time
	formatDate(ord.observationEnd)+"|"+//observation end date time
	"|"+//collection volume - observation only, leave blank
	formatProvider(ord.provider)+"|"+//collector identifier
	"L|"+//specimen action code = L lab
	"|"+//danger code
	"|"+//relevant clinical info
	"|"+//specimen recieved date time
	"|"+//specimen source
	formatProvider(ord.provider)+"|"+//ordering provider
	ord.provider.phone.work+"|"+//order callback number
	"\n";
	return obr.toUpperCase();
}
function OBX(obs){

	var obx = "OBX|1|"+//
	"NM|"+//value type
	obs.id+"|"+//observation identifier
	"|"+//observation sub-ID - for grouping, leaving blank
	obs.value+"|"+//observation value
	obs.units+"|"+//units
	obs.referenceRange+"|"+//reference range
	"|"+//abnormal flags
	"|"+//probability
	obs.natureOfAbnormal+"|"+//nature of abnormal test
	obs.obsResultStatus+"|"+//observ result status
	formatDate(adjustDate(obs.obsDate,-1,-7))+"|"+//date last obs normal values
	"|"+//user defined access checks
	formatDate(obs.obsDate)+"|"+//date/time of observation
	"CB^Pathology Lab X|"+//producersId
	"|"+//responsible observer
	"|"+//observation method
	"\n";
	return obx;
}
function mdmOBX(eventType){
	if (eventType == "T01"){return "";}
	else if (eventType !== "T02"){throw("Not a valid event type for MDM. Supported event types are: T01, T02. Try sending MDM^T02 to receive a sample message");}
	var encodedDoc = "";
	var obx = "OBX|1|" +
			"ED|" + // could be ED for epic base64
			randomInt(10)+"^"+"CLINICAL SUMMARY|" + //
			"1|" + //
			"^RTF^^base64^"+encodedDoc+"|" + //
			"|" + //
			"\n";
	return obx;
}
function TXA(){
	var prov = provider();
	var activityDate = randomDate(2016,2016);
	var authentication = valFromList(["DO","IP","PA","AU","LA"]);
	var authenticationPerson = "";
	var authenticationTime = "";
	if (authentication == "AU" || authentication == "PA" || authentication == "LA"){
		authenticationPerson = formatProvider(prov);
		authenticationTime = formatDate(activityDate);
	}
	var txa = "TXA|1|"+
	"PN|"+//document type = progress note
	"FT|"+//document content presentation - formatted text 
	formatDate(activityDate)+"|"+//activity date / time
	formatProvider(prov)+"|"+//activity provider
	formatDate(activityDate)+"|"+//origination date time
	formatDate(activityDate)+"|"+//transcription date time
	"|"+//edit date time
	formatProvider(prov)+"|"+//originator code /name
	formatProvider(prov)+"|"+//assigned document authenticator
	"|"+//transcriptionist code
	randomLetter()+randomInt(10)+"|"+//unique document number
	randomLetter()+randomInt(10)+"|"+//parent document number
	"|"+//placer oder number
	"|"+//filler order number
	"Progress Note "+formatDate(activityDate)+"|"+//unique document file name
	authentication+"|"+//document completion status
	"U|"+//document confidentiality status
	"AV|"+//document availability status
	"AC|"+//document storage status
	"|"+//document change reason
	authenticationPerson+"|"+//authentication person
	authenticationTime+"|"+//authentication time stamp
	"\n";
	return txa.toUpperCase();
}
function DG1(){
	var diag = diagnosis();
	var dg1 = "DG1|1|" +
			"|" + //diagnosis coding method - NOT USED
			diag+"|" + //diagnosis code
			"|" + //description - NOT USED
			formatDate(randomDate(2016,2016))+"|" + //date/time
			"F|" + //type = final
			"\n";
	return dg1.toUpperCase();
}
function PR1(){
	var proc = procedure();
	var pr1 = "PR1|1|" +
			"|"+//procedure coding method NOT USED
			proc+"|"+//procedure code
			"|"+//procedure description NOT USED
			formatDate(randomDate(2016,2016))+"|"+//procedure date
			"|"+//functional type
			"D|"+//functional typediagnostic procedure
			"|"+//minutes
			"\n"; //other elements for backwards compatibility
	return pr1.toUpperCase();
}
function AL1(){
	var allergy = valFromList([
		{"id":"F"+randomInt(9),"text":"Penicillins","detail":"PENICILLINS"},
		{"id":"F"+randomInt(9),"text":"RIVAROXABAN","detail":"FROM XARELTO"},
		{"id":"F"+randomInt(9),"text":"VILANTEROL","detail":"FROM ANORO ELLIPTA"},
		{"id":"F"+randomInt(9),"text":"UMECLIDINIUM","detail":"FROM ANORO ELLIPTA"},
		{"id":"F"+randomInt(9),"text":"ADHESIVE","detail":"ADHESIVE"},
		{"id":"F"+randomInt(9),"text":"NICKEL","detail":"NICKEL"},
		{"id":"F"+randomInt(9),"text":"MORPHINE","detail":"MORPHINE"},
		{"id":"F"+randomInt(9),"text":"CODEINE","detail":"CODEINE"},
		{"id":"F"+randomInt(9),"text":"AZITHROMYCIN","detail":"FROM ZITHROMAX Z-PAK"}
	]);
	var al1 = "AL1|1|" +
			"DA|"+//allergy type = drug
			allergy.id+"^"+allergy.text+"^^"+allergy.detail+"|"+//allergy code /mnemonic description
			valFromList(["SV","MO","MI"])+"|"+//allergy severity
			valFromList(["Rash","Breathing difficulty","Light sensitivity","Hives","Nausea",])+"|"+//allergy reaction
			formatDate(randomDate(2012,2016))+"|"+//identification date
			"\n";
	return al1.toUpperCase();
}
