				"The patient can't see what I typed in the chart",
				"The patient can't see what I typed in the chart",
				"I liked the old paper charts better"
			])+"\n";
	return nte;
}
function IN1(ins){

	var in1 = "IN1|" +
	"1|" + //Set ID - Insurance
	ins.planId+"|" + //Insurance Plan ID
	ins.company.id+"|" + //Insurance Company ID
	ins.company.name+"|" + //Insurance Company Name
	ins.company.address.street + "^"+"MADISON"+"^"+ins.company.address.state+"^"+ins.company.address.zip+"|" + //Insurance Company Address
	ins.company.contactPerson+"|" + //Insurance Co. Contact Ppers
	ins.company.contactPhone+"|" + //Insurance Co Phone Number
	ins.groupNumber+"|" + //Group Number
	ins.groupName+"|" + //Group Name
	ins.groupEmpId+"|" + //Insured's group employer ID
	ins.groupEmployer+"|" + //Insured's Group Emp Name
	formatDate(ins.effectiveDate)+"|" + //Plan Effective Date
	valFromList([formatDate(ins.expirationDate),""])+"|" + //Plan Expiration Date
	ins.authorizationInfo+"|" + //Authorization Information
	ins.planType+"|" + //Plan Type
	ins.insured.name.last + "^" + ins.insured.name.first+"^"+ins.insured.name.initial+"|" + //Name of Insured
	"SELF|" + //Insured's Relationship to Patient
	ins.insured.dob+"|" + //Insured's Date of Birth
	ins.insured.address.street + "^"+"MADISON"+"^"+ins.insured.address.state+"^"+ins.insured.address.zip+"|" + //Insured's Address
	valFromList(["Y","N","M"])+"|" + //Assignment of Benefits
	valFromList(["CO","IN"])+"|" + //Coordination of Benefits
	"1|" + //Coord of Ben. Priority
	"N|" + //Notice of Admission Code
	"|" + //Notice of Admission Date
	"N|" + //Rpt of Eigibility Code
	"|" + //Rpt of Eligibility Date
	YN()+"|" + //Release Information Code
	"|" + //Pre-Admit Cert
	formatDate(randomDate(2016,2017))+"|" + //Verification Date/Time
	"|" + //Verification By
	valFromList(["S","U"])+"|" + //Type of Agreement Code
	"|" + //Billing Status
	"|" + //Lifetime Reserve Days
	"|" + //Delay before lifetime reserve days
	"|" + //Company Plan Code
	ins.policyNumber+"|" + //Policy Number
	ins.deductible+"|" + //Policy Deductible
	"|" + //Policy Limit - Amount
	"|" + //Policy Limit - Days
	"|" + //Room Rate - Semi-Private
	"|" + //Room Rate - Private
	ins.insured.jobStatus+"|" + //Insured's Employment Status
	ins.insured.sex+"|" + //Insured's Sex
	ins.insured.employer.address.street + "^"+"MADISON"+"^"+ins.insured.employer.address.state+"^"+ins.insured.employer.address.zip+"|" + //Insured's Employer Address
	"|" + //Verification Status
	ins.priorPlanId+"|" + //Prior Insurance Plan ID
	ins.coverageType+"|" + //Coverage Type
	"|" + //Handicap
	ins.insured.id.external+"|\n";  //Insured's ID Number
	return in1.toUpperCase();

}
